import { useState, useEffect, useRef } from 'react';
import { useSelector } from 'react-redux';
import {
  useConversations,
  useMessages,
  useSendMessage,
  useMarkAsRead,
} from '@/hooks/useMessageQuery';
import toast from 'react-hot-toast';

import ChatSidebar from './ChatSidebar';
import ChatWindow from './ChatWindow';

const Messages = () => {
  const { user: currentUser } = useSelector(state => state.auth);

  const [selectedChat, setSelectedChat] = useState(null);
  const [messageInput, setMessageInput] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const messagesEndRef = useRef(null);

  const { data: conversationsData, isLoading: conversationsLoading } =
    useConversations({ page: 1, limit: 20 });

  const selectedChatId = selectedChat?._id || selectedChat?.id;
  const { data: messagesData, isLoading: messagesLoading } = useMessages({
    conversationId: selectedChatId,
    page: 1,
  });

  const sendMessageMutation = useSendMessage();
  const markAsReadMutation = useMarkAsRead();

  useEffect(() => {
    if (selectedChatId) {
      markAsReadMutation.mutate(selectedChatId);
    }
  }, [selectedChatId]);

  // Scroll to bottom
  const messages = messagesData?.messages || messagesData || [];
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, selectedChat]);

  const handleSend = async e => {
    e.preventDefault();
    if (!messageInput.trim() || !selectedChatId) return;

    try {
      await sendMessageMutation.mutateAsync({
        conversationId: selectedChatId,
        content: messageInput,
        type: 'text',
      });
      setMessageInput('');
    } catch (error) {
      toast.error(error?.response?.data?.message || 'Gửi tin nhắn thất bại');
    }
  };

  const loading = conversationsLoading || messagesLoading;
  const conversations =
    conversationsData?.conversations || conversationsData || [];

  return (
    <div className="h-[calc(100vh-8rem)] bg-white dark:bg-neutral-900 rounded-2xl border border-neutral-200 dark:border-neutral-800 flex overflow-hidden shadow-sm">
      <ChatSidebar
        conversations={conversations}
        loading={loading}
        selectedChat={selectedChat}
        setSelectedChat={setSelectedChat}
        currentUser={currentUser}
        searchTerm={searchTerm}
        setSearchTerm={setSearchTerm}
      />

      <ChatWindow
        selectedChat={selectedChat}
        setSelectedChat={setSelectedChat}
        messages={messages}
        currentUser={currentUser}
        messageInput={messageInput}
        setMessageInput={setMessageInput}
        handleSend={handleSend}
        messagesEndRef={messagesEndRef}
      />
    </div>
  );
};

export default Messages;

import { X, AlertTriangle, CheckCircle, Info, AlertCircle } from 'lucide-react';

/**
 * Modal Component
 */
export function Modal({ isOpen, onClose, title, children, size = 'md' }) {
  if (!isOpen) return null;

  const sizeClasses = {
    sm: 'max-w-sm',
    md: 'max-w-md',
    lg: 'max-w-lg',
    xl: 'max-w-xl',
    '2xl': 'max-w-2xl',
  };

  return (
    <div className="fixed inset-0 bg-black/30 dark:bg-black/50 flex items-center justify-center z-50 p-4">
      <div
        className={`bg-white dark:bg-neutral-900 rounded-2xl w-full ${sizeClasses[size]} border border-neutral-100 dark:border-neutral-800`}
      >
        <div className="flex items-center justify-between p-5 border-b border-neutral-100 dark:border-neutral-800">
          <h2 className="text-base font-semibold text-neutral-800 dark:text-white">
            {title}
          </h2>
          <button
            onClick={onClose}
            className="p-1.5 hover:bg-neutral-100 dark:hover:bg-neutral-800 rounded-lg transition-colors text-neutral-500"
          >
            <X size={18} />
          </button>
        </div>
        <div className="p-5">{children}</div>
      </div>
    </div>
  );
}

/**
 * Confirm Modal Component
 */
export function ConfirmModal({
  isOpen,
  onClose,
  onConfirm,
  title,
  message,
  confirmText = 'Xác nhận',
  cancelText = 'Hủy',
  type = 'danger',
}) {
  if (!isOpen) return null;

  const typeConfig = {
    danger: {
      icon: AlertTriangle,
      iconBg: 'bg-rose-50 dark:bg-rose-500/10',
      iconColor: 'text-rose-600 dark:text-rose-400',
      confirmBg: 'bg-rose-600 hover:bg-rose-700',
    },
    warning: {
      icon: AlertCircle,
      iconBg: 'bg-amber-50 dark:bg-amber-500/10',
      iconColor: 'text-amber-600 dark:text-amber-400',
      confirmBg: 'bg-amber-600 hover:bg-amber-700',
    },
    success: {
      icon: CheckCircle,
      iconBg: 'bg-emerald-50 dark:bg-emerald-500/10',
      iconColor: 'text-emerald-600 dark:text-emerald-400',
      confirmBg: 'bg-emerald-600 hover:bg-emerald-700',
    },
    info: {
      icon: Info,
      iconBg: 'bg-blue-50 dark:bg-blue-500/10',
      iconColor: 'text-blue-600 dark:text-blue-400',
      confirmBg: 'bg-blue-600 hover:bg-blue-700',
    },
  };

  const config = typeConfig[type];
  const Icon = config.icon;

  return (
    <div className="fixed inset-0 bg-black/30 dark:bg-black/50 flex items-center justify-center z-50 p-4">
      <div className="bg-white dark:bg-neutral-900 rounded-2xl w-full max-w-sm p-5 border border-neutral-100 dark:border-neutral-800">
        <div className="flex items-start gap-3 mb-4">
          <div className={`p-2.5 rounded-xl ${config.iconBg}`}>
            <Icon size={20} className={config.iconColor} strokeWidth={1.5} />
          </div>
          <div className="flex-1">
            <h2 className="text-base font-semibold text-neutral-800 dark:text-white">
              {title}
            </h2>
            <p className="text-sm text-neutral-500 dark:text-neutral-400 mt-1">
              {message}
            </p>
          </div>
        </div>

        <div className="flex gap-2 mt-5">
          <button
            onClick={onClose}
            className="flex-1 px-4 py-2.5 rounded-xl border border-neutral-200 dark:border-neutral-700 hover:bg-neutral-100 dark:hover:bg-neutral-800 transition-colors text-sm font-medium text-neutral-700 dark:text-neutral-300"
          >
            {cancelText}
          </button>
          <button
            onClick={() => {
              onConfirm();
              onClose();
            }}
            className={`flex-1 px-4 py-2.5 rounded-xl text-white transition-colors text-sm font-medium ${config.confirmBg}`}
          >
            {confirmText}
          </button>
        </div>
      </div>
    </div>
  );
}

/**
 * Badge Component
 */
export function Badge({ children, variant = 'default' }) {
  const variants = {
    default:
      'bg-neutral-100 dark:bg-neutral-800 text-neutral-600 dark:text-neutral-300',
    primary:
      'bg-violet-50 dark:bg-violet-500/10 text-violet-600 dark:text-violet-400',
    success:
      'bg-emerald-50 dark:bg-emerald-500/10 text-emerald-600 dark:text-emerald-400',
    warning:
      'bg-amber-50 dark:bg-amber-500/10 text-amber-600 dark:text-amber-400',
    danger: 'bg-rose-50 dark:bg-rose-500/10 text-rose-600 dark:text-rose-400',
    info: 'bg-blue-50 dark:bg-blue-500/10 text-blue-600 dark:text-blue-400',
  };

  return (
    <span
      className={`inline-flex items-center px-2 py-0.5 rounded-md text-xs font-medium ${variants[variant]}`}
    >
      {children}
    </span>
  );
}

/**
 * Button Component
 */
export function Button({
  children,
  variant = 'primary',
  size = 'md',
  disabled = false,
  loading = false,
  icon: Icon,
  onClick,
  className = '',
  ...props
}) {
  const variants = {
    primary:
      'bg-neutral-900 dark:bg-white text-white dark:text-neutral-900 hover:bg-neutral-800 dark:hover:bg-neutral-100',
    secondary:
      'border border-neutral-200 dark:border-neutral-700 text-neutral-700 dark:text-neutral-300 hover:bg-neutral-100 dark:hover:bg-neutral-800',
    danger: 'bg-rose-600 text-white hover:bg-rose-700',
    success: 'bg-emerald-600 text-white hover:bg-emerald-700',
    ghost:
      'text-neutral-600 dark:text-neutral-400 hover:bg-neutral-100 dark:hover:bg-neutral-800',
  };

  const sizes = {
    sm: 'px-3 py-1.5 text-xs',
    md: 'px-4 py-2 text-sm',
    lg: 'px-5 py-2.5 text-sm',
  };

  return (
    <button
      onClick={onClick}
      disabled={disabled || loading}
      className={`inline-flex items-center justify-center gap-2 rounded-xl font-medium transition-colors disabled:opacity-50 disabled:cursor-not-allowed ${variants[variant]} ${sizes[size]} ${className}`}
      {...props}
    >
      {loading ? (
        <div className="w-4 h-4 border-2 border-current border-t-transparent rounded-full animate-spin" />
      ) : (
        Icon && <Icon size={size === 'sm' ? 14 : 16} strokeWidth={1.5} />
      )}
      {children}
    </button>
  );
}

/**
 * Input Component
 */
export function Input({ label, error, icon: Icon, className = '', ...props }) {
  return (
    <div className={className}>
      {label && (
        <label className="block text-sm font-medium text-neutral-700 dark:text-neutral-300 mb-1.5">
          {label}
        </label>
      )}
      <div className="relative">
        {Icon && (
          <Icon
            size={16}
            className="absolute left-3.5 top-1/2 -translate-y-1/2 text-neutral-400"
            strokeWidth={1.5}
          />
        )}
        <input
          className={`w-full ${
            Icon ? 'pl-10' : 'pl-4'
          } pr-4 py-2.5 rounded-xl border ${
            error
              ? 'border-rose-400 focus:ring-rose-200'
              : 'border-neutral-200 dark:border-neutral-700 focus:ring-neutral-200 dark:focus:ring-neutral-700'
          } bg-white dark:bg-neutral-800 text-neutral-800 dark:text-neutral-100 text-sm focus:outline-none focus:ring-2 transition-all`}
          {...props}
        />
      </div>
      {error && <p className="mt-1 text-xs text-rose-500">{error}</p>}
    </div>
  );
}

/**
 * Select Component
 */
export function Select({ label, options, className = '', ...props }) {
  return (
    <div className={className}>
      {label && (
        <label className="block text-sm font-medium text-neutral-700 dark:text-neutral-300 mb-1.5">
          {label}
        </label>
      )}
      <select
        className="w-full px-4 py-2.5 rounded-xl border border-neutral-200 dark:border-neutral-700 bg-white dark:bg-neutral-800 text-neutral-800 dark:text-neutral-100 text-sm focus:outline-none focus:ring-2 focus:ring-neutral-200 dark:focus:ring-neutral-700 transition-all"
        {...props}
      >
        {options.map(option => (
          <option key={option.value} value={option.value}>
            {option.label}
          </option>
        ))}
      </select>
    </div>
  );
}

/**
 * Toggle Component
 */
export function Toggle({ checked, onChange, label, description }) {
  return (
    <div className="flex items-center justify-between">
      <div>
        {label && (
          <p className="font-medium text-black dark:text-white">{label}</p>
        )}
        {description && (
          <p className="text-sm text-neutral-500 dark:text-neutral-400">
            {description}
          </p>
        )}
      </div>
      <button
        onClick={() => onChange(!checked)}
        className={`relative w-12 h-6 rounded-full transition-colors ${
          checked
            ? 'bg-black dark:bg-white'
            : 'bg-neutral-300 dark:bg-neutral-600'
        }`}
      >
        <div
          className={`absolute top-1 w-4 h-4 rounded-full transition-all ${
            checked ? 'right-1 bg-white dark:bg-black' : 'left-1 bg-white'
          }`}
        />
      </button>
    </div>
  );
}

/**
 * Avatar Component
 */
export function Avatar({ src, alt, size = 'md', fallback }) {
  const sizes = {
    sm: 'w-8 h-8',
    md: 'w-10 h-10',
    lg: 'w-12 h-12',
    xl: 'w-16 h-16',
  };

  return (
    <div
      className={`${sizes[size]} rounded-full border-2 border-neutral-200 dark:border-neutral-700 overflow-hidden bg-neutral-100 dark:bg-neutral-800 flex items-center justify-center`}
    >
      {src ? (
        <img src={src} alt={alt} className="w-full h-full object-cover" />
      ) : (
        <span className="text-neutral-500 dark:text-neutral-400 font-medium">
          {fallback || alt?.charAt(0)?.toUpperCase()}
        </span>
      )}
    </div>
  );
}

/**
 * Card Component
 */
export function Card({ children, className = '', ...props }) {
  return (
    <div
      className={`bg-white dark:bg-neutral-900 rounded-2xl border border-neutral-200 dark:border-neutral-800 ${className}`}
      {...props}
    >
      {children}
    </div>
  );
}

/**
 * Card Header Component
 */
export function CardHeader({ children, className = '' }) {
  return (
    <div
      className={`p-5 border-b border-neutral-200 dark:border-neutral-700 ${className}`}
    >
      {children}
    </div>
  );
}

/**
 * Card Content Component
 */
export function CardContent({ children, className = '' }) {
  return <div className={`p-5 ${className}`}>{children}</div>;
}

/**
 * Stat Card Component
 */
export function StatCard({
  title,
  value,
  icon: Icon,
  trend,
  trendValue,
  color = 'neutral',
}) {
  const colors = {
    neutral: {
      iconBg: 'bg-neutral-100 dark:bg-neutral-800',
      iconColor: 'text-neutral-600 dark:text-neutral-400',
    },
    blue: {
      iconBg: 'bg-blue-100 dark:bg-blue-900/30',
      iconColor: 'text-blue-600 dark:text-blue-400',
    },
    green: {
      iconBg: 'bg-green-100 dark:bg-green-900/30',
      iconColor: 'text-green-600 dark:text-green-400',
    },
    red: {
      iconBg: 'bg-red-100 dark:bg-red-900/30',
      iconColor: 'text-red-600 dark:text-red-400',
    },
    yellow: {
      iconBg: 'bg-yellow-100 dark:bg-yellow-900/30',
      iconColor: 'text-yellow-600 dark:text-yellow-400',
    },
    purple: {
      iconBg: 'bg-purple-100 dark:bg-purple-900/30',
      iconColor: 'text-purple-600 dark:text-purple-400',
    },
  };

  const colorConfig = colors[color];

  return (
    <Card className="p-5">
      <div className="flex items-center justify-between mb-3">
        <div className={`p-2 rounded-lg ${colorConfig.iconBg}`}>
          <Icon size={20} className={colorConfig.iconColor} />
        </div>
        {trend && (
          <span
            className={`text-sm font-medium ${
              trend === 'up'
                ? 'text-green-600 dark:text-green-400'
                : 'text-red-600 dark:text-red-400'
            }`}
          >
            {trend === 'up' ? '+' : '-'}
            {trendValue}%
          </span>
        )}
      </div>
      <p className="text-neutral-500 dark:text-neutral-400 text-sm">{title}</p>
      <p className="text-2xl font-bold text-black dark:text-white mt-1">
        {value}
      </p>
    </Card>
  );
}

/**
 * Empty State Component
 */
export function EmptyState({ icon: Icon, title, description, action }) {
  return (
    <div className="flex flex-col items-center justify-center py-12 px-4 text-center">
      {Icon && (
        <div className="p-4 bg-neutral-100 dark:bg-neutral-800 rounded-full mb-4">
          <Icon size={32} className="text-neutral-400" />
        </div>
      )}
      <h3 className="text-lg font-semibold text-black dark:text-white mb-2">
        {title}
      </h3>
      {description && (
        <p className="text-neutral-500 dark:text-neutral-400 mb-4 max-w-md">
          {description}
        </p>
      )}
      {action}
    </div>
  );
}

/**
 * Loading Spinner Component
 */
export function Spinner({ size = 'md' }) {
  const sizes = {
    sm: 'w-4 h-4 border-2',
    md: 'w-6 h-6 border-2',
    lg: 'w-8 h-8 border-3',
  };

  return (
    <div
      className={`${sizes[size]} border-neutral-300 dark:border-neutral-600 border-t-black dark:border-t-white rounded-full animate-spin`}
    />
  );
}

/**
 * Skeleton Loader Component
 */
export function Skeleton({ className = '' }) {
  return (
    <div
      className={`animate-pulse bg-neutral-200 dark:bg-neutral-700 rounded ${className}`}
    />
  );
}

import { useState } from 'react';
import { NavLink, Link, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import {
  Home,
  MessageCircle,
  Moon,
  Search,
  Sun,
  Settings,
  Bell,
  User,
  LogOut,
  Bookmark,
  Sparkles,
  PenSquare,
  ChevronLeft,
  ChevronRight,
} from 'lucide-react';
import { logout } from '@/redux/actions/authActions';
import toast from 'react-hot-toast';
import { useUnreadCount } from '@/hooks/useNotificationQuery';
import { useUnreadMessagesCount } from '@/hooks/useMessageQuery';

// Custom Nav Item
const NavItem = ({
  to,
  icon: Icon,
  label,
  onClick,
  badge,
  isActive: forceActive,
  collapsed,
}) => {
  return (
    <NavLink to={to || '#'} onClick={onClick} className="group w-full">
      {({ isActive }) => {
        const active = forceActive !== undefined ? forceActive : isActive;
        return (
          <div
            className={`flex items-center gap-3 px-3 py-2.5 rounded-full transition-all w-full ${
              active
                ? 'bg-primary text-primary-foreground font-medium'
                : 'text-neutral-600 dark:text-neutral-400 hover:bg-neutral-100 dark:hover:bg-neutral-800 hover:text-black dark:hover:text-white'
            } ${collapsed ? 'justify-center px-2' : ''}`}
            title={collapsed ? label : undefined}
          >
            <div className="relative flex-shrink-0">
              <Icon size={20} strokeWidth={active ? 2.5 : 2} />
              {badge > 0 && (
                <span className="absolute -top-1.5 -right-1.5 min-w-[16px] h-[16px] bg-red-500 text-white text-[10px] font-medium rounded-full flex items-center justify-center px-1">
                  {badge > 99 ? '99+' : badge}
                </span>
              )}
            </div>
            {!collapsed && <span className="text-sm truncate">{label}</span>}
          </div>
        );
      }}
    </NavLink>
  );
};

export default function Navigate({ mobile = false, onCollapsedChange }) {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { user } = useSelector(state => state.auth);
  /* State */
  const [collapsed, setCollapsed] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(() => {
    const saved = localStorage.getItem('theme');
    if (saved) return saved === 'dark';
    return window.matchMedia('(prefers-color-scheme: dark)').matches;
  });

  const DEFAULT_USER = {
    name: 'Người dùng',
    username: 'user',
    avatar: 'https://via.placeholder.com/40',
  };

  /* Handlers */
  const handleLogout = async () => {
    await dispatch(logout());
    toast.success('Đăng xuất thành công');
    navigate('/auth/login');
  };

  const toggleTheme = () => {
    const newDark = !isDarkMode;
    setIsDarkMode(newDark);
    localStorage.setItem('theme', newDark ? 'dark' : 'light');
    if (newDark) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  };

  const toggleSidebar = () => {
    const newCollapsed = !collapsed;
    setCollapsed(newCollapsed);
    onCollapsedChange?.(newCollapsed);
  };

  const { data: messageUnreadCount = 0 } = useUnreadMessagesCount();

  const { data: notificationUnreadCount = 0 } = useUnreadCount();

  const navItems = [
    { icon: Home, path: '/', label: 'Home' },
    { icon: Search, path: '/explore', label: 'Explore' },
    {
      icon: Bell,
      path: '/notifications',
      label: 'Notifications',
      badge: notificationUnreadCount,
    },
    {
      icon: MessageCircle,
      path: '/messages',
      label: 'Messages',
      badge: messageUnreadCount,
    },
    { icon: Bookmark, path: '/saved', label: 'Saved' },
    { icon: User, path: '/profile', label: 'Profile' },
  ];

  // Mobile Bottom Navigation
  if (mobile) {
    return (
      <div className="h-[56px] w-full flex justify-around items-center bg-white/95 dark:bg-neutral-900/95 backdrop-blur-xl">
        {navItems.slice(0, 5).map((item, i) => (
          <NavLink
            key={i}
            to={item.path}
            className={({ isActive }) =>
              `relative flex flex-col items-center justify-center p-2 rounded-full transition-all ${
                isActive
                  ? 'text-black dark:text-white'
                  : 'text-neutral-400 hover:text-black dark:hover:text-white'
              }`
            }
          >
            {({ isActive }) => (
              <>
                {isActive && (
                  <div className="absolute -top-1 w-6 h-0.5 rounded-full bg-black dark:bg-white" />
                )}
                <div className="relative">
                  <item.icon size={22} strokeWidth={isActive ? 2.5 : 2} />
                  {item.badge > 0 && (
                    <span className="absolute -top-1.5 -right-1.5 min-w-[16px] h-[16px] bg-red-500 text-white text-[10px] font-medium rounded-full flex items-center justify-center px-1">
                      {item.badge > 99 ? '99+' : item.badge}
                    </span>
                  )}
                </div>
              </>
            )}
          </NavLink>
        ))}
      </div>
    );
  }

  // Desktop Sidebar
  return (
    <div
      className={`h-full flex flex-col py-6 bg-white dark:bg-neutral-900 transition-all duration-300 ease-in-out ${
        collapsed ? 'px-2 w-[72px]' : 'px-4 w-full'
      }`}
    >
      {/* Logo */}
      <Link
        to="/"
        className={`flex items-center gap-3 mb-8 ${
          collapsed ? 'justify-center px-0' : 'px-1'
        }`}
      >
        <div className="w-10 h-10 rounded-full bg-black dark:bg-white flex items-center justify-center flex-shrink-0">
          <Sparkles size={18} className="text-white dark:text-black" />
        </div>
        {!collapsed && (
          <span className="text-xl font-semibold tracking-tight text-black dark:text-white">
            YiBu
          </span>
        )}
      </Link>

      {/* Collapse Toggle */}
      <button
        onClick={toggleSidebar}
        className="mb-4 p-2 rounded-full bg-neutral-100 dark:bg-neutral-800 hover:bg-neutral-200 dark:hover:bg-neutral-700 transition-all self-center"
        title={collapsed ? 'Expand sidebar' : 'Collapse sidebar'}
      >
        {collapsed ? (
          <ChevronRight size={16} className="text-neutral-500" />
        ) : (
          <ChevronLeft size={16} className="text-neutral-500" />
        )}
      </button>

      {/* Navigation */}
      <nav className="flex flex-col gap-1 flex-1">
        {navItems.map((item, i) => (
          <NavItem
            key={i}
            to={item.path}
            icon={item.icon}
            label={item.label}
            badge={item.badge}
            collapsed={collapsed}
          />
        ))}

        {/* Settings */}
        <NavItem
          to="/settings"
          icon={Settings}
          label="Settings"
          collapsed={collapsed}
        />

        {/* Theme Toggle */}
        <div
          onClick={toggleTheme}
          className={`flex items-center gap-3 px-3 py-2.5 rounded-full transition-all cursor-pointer text-neutral-600 dark:text-neutral-400 hover:bg-neutral-100 dark:hover:bg-neutral-800 hover:text-black dark:hover:text-white ${
            collapsed ? 'justify-center px-2' : ''
          }`}
          title={
            collapsed ? (isDarkMode ? 'Light mode' : 'Dark mode') : undefined
          }
        >
          <div className="flex-shrink-0">
            {isDarkMode ? <Sun size={20} /> : <Moon size={20} />}
          </div>
          {!collapsed && (
            <span className="text-sm">
              {isDarkMode ? 'Light mode' : 'Dark mode'}
            </span>
          )}
        </div>

        {/* Create Post Button */}
        {collapsed ? (
          <button
            className="mt-6 w-10 h-10 rounded-full bg-primary flex items-center justify-center mx-auto hover:opacity-80 transition-opacity"
            title="Create Post"
          >
            <PenSquare size={18} className="text-primary-foreground" />
          </button>
        ) : (
          <button className="mt-6 flex items-center justify-center gap-2 py-2.5 px-4 w-full bg-primary text-primary-foreground rounded-full font-medium text-sm hover:opacity-90 transition-opacity">
            <PenSquare size={16} />
            <span>Create Post</span>
          </button>
        )}
      </nav>

      {/* User Profile Card */}
      <div className="mt-auto pt-4">
        <div
          className={`flex items-center gap-3 p-2 rounded-full hover:bg-neutral-100 dark:hover:bg-neutral-800 transition-all group ${
            collapsed ? 'justify-center' : ''
          }`}
          title={collapsed ? user?.name || DEFAULT_USER.name : undefined}
        >
          <Link to="/profile" className="relative flex-shrink-0">
            <img
              src={user?.avatar || DEFAULT_USER.avatar}
              alt="Profile"
              className="w-10 h-10 rounded-full object-cover"
            />
            <div className="absolute -bottom-0.5 -right-0.5 w-3 h-3 bg-green-500 rounded-full" />
          </Link>
          {!collapsed && (
            <>
              <Link to="/profile" className="flex-1 min-w-0">
                <p className="text-sm font-medium text-black dark:text-white truncate leading-tight">
                  {user?.name || DEFAULT_USER.name}
                </p>
                <p className="text-xs text-neutral-500 truncate leading-tight mt-0.5">
                  @{user?.username || DEFAULT_USER.username}
                </p>
              </Link>
              <button
                onClick={handleLogout}
                className="p-2 rounded-full hover:bg-neutral-200 dark:hover:bg-neutral-700 transition-colors"
                title="Đăng xuất"
              >
                <LogOut
                  size={16}
                  className="text-neutral-400 hover:text-red-500"
                />
              </button>
            </>
          )}
        </div>
        {collapsed && (
          <button
            onClick={handleLogout}
            className="w-full mt-2 p-2 rounded-full hover:bg-neutral-100 dark:hover:bg-neutral-800 transition-colors flex justify-center"
            title="Đăng xuất"
          >
            <LogOut size={18} className="text-neutral-400 hover:text-red-500" />
          </button>
        )}
      </div>
    </div>
  );
}

import { useState, useRef, useEffect, useCallback, useMemo } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import {
  MessageCircle,
  X,
  ArrowLeft,
  MoreHorizontal,
  Send,
  Image as ImageIcon,
  Smile,
  CheckCheck,
  Loader2,
  Trash2,
  Copy,
  Flag,
  Phone,
  Video,
  Info,
} from 'lucide-react';
import toast from 'react-hot-toast';
import {
  useConversations,
  useConversationById,
  useMessages,
  useMarkAsRead,
  useSendMessage,
  useDeleteMessage,
  useUpdateGroup,
  useAddGroupMember,
  useRemoveGroupMember,
  useLeaveGroup,
} from '@/hooks/useMessageQuery';
import { useSocketContext } from '@/contexts/SocketContext';
import { lazy, Suspense } from 'react';

// Lazy load modals
const ReportModal = lazy(() =>
  import('../report/ReportModal').then(module => ({ default: module.default }))
);
const GroupInfoModal = lazy(() => import('./GroupInfoModal'));

// --- Sub-components ---

const TypingIndicator = ({ username }) => (
  <div className="flex items-center gap-3 px-4 py-3 animate-fade-in transition-all">
    <div className="flex gap-1.5 bg-surface-secondary px-3 py-2 rounded-2xl rounded-bl-none">
      <div className="w-1.5 h-1.5 bg-secondary rounded-full animate-bounce [animation-delay:-0.3s]" />
      <div className="w-1.5 h-1.5 bg-secondary rounded-full animate-bounce [animation-delay:-0.15s]" />
      <div className="w-1.5 h-1.5 bg-secondary rounded-full animate-bounce" />
    </div>
    <span className="text-[10px] font-bold text-text-tertiary uppercase tracking-tight">
      {username} đang soạn...
    </span>
  </div>
);

const MessageBubble = ({
  message,
  isOwn,
  showAvatar,
  avatar,
  onDelete,
  onReport,
}) => {
  const [showMenu, setShowMenu] = useState(false);

  const handleCopy = () => {
    navigator.clipboard.writeText(message.content);
    toast.success('Đã sao chép tin nhắn');
    setShowMenu(false);
  };

  return (
    <div
      className={`flex w-full mb-1 ${isOwn ? 'justify-end' : 'justify-start'}`}
    >
      <div
        className={`flex max-w-[85%] sm:max-w-[70%] group ${
          isOwn ? 'flex-row-reverse' : 'flex-row'
        } items-end gap-2 relative`}
      >
        {/* Avatar */}
        {!isOwn && (
          <div className="w-8 h-8 flex-shrink-0">
            {showAvatar ? (
              <img
                src={avatar || 'https://via.placeholder.com/150'}
                alt=""
                className="w-8 h-8 rounded-full object-cover"
              />
            ) : (
              <div className="w-8" />
            )}
          </div>
        )}

        {/* Bubble */}
        <div className="flex flex-col gap-1">
          <div
            className={`relative px-4 py-2.5 text-sm transition-all duration-200 ${
              isOwn
                ? 'bg-primary text-primary-foreground rounded-2xl rounded-br-none'
                : 'bg-surface-secondary text-content rounded-2xl rounded-bl-none'
            }`}
            onContextMenu={e => {
              e.preventDefault();
              setShowMenu(true);
            }}
          >
            {message.content && (
              <p className="whitespace-pre-wrap leading-relaxed break-words font-medium">
                {message.content}
              </p>
            )}

            {message.media?.length > 0 && (
              <div className="mt-2 space-y-2">
                {message.media.map((item, i) => (
                  <img
                    key={i}
                    src={item.url}
                    alt=""
                    className="rounded-xl max-w-full h-auto object-cover"
                  />
                ))}
              </div>
            )}

            <div
              className={`flex items-center justify-end gap-1 mt-1 ${
                isOwn ? 'opacity-60' : 'text-text-tertiary'
              } text-[10px] font-medium`}
            >
              <span>
                {new Date(message.createdAt).toLocaleTimeString([], {
                  hour: '2-digit',
                  minute: '2-digit',
                })}
              </span>
              {isOwn && (
                <div className="ml-0.5">
                  {message.status === 'read' || message.isRead ? (
                    <CheckCheck size={12} />
                  ) : (
                    <CheckCheck size={12} className="opacity-40" />
                  )}
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Action Button */}
        <button
          onClick={() => setShowMenu(!showMenu)}
          className={`p-1.5 rounded-full hover:bg-surface-hover opacity-0 group-hover:opacity-100 transition-all ${
            isOwn ? 'mr-1' : 'ml-1'
          }`}
        >
          <MoreHorizontal size={14} className="text-text-tertiary" />
        </button>

        {/* Floating Menu */}
        {showMenu && (
          <>
            <div
              className="fixed inset-0 z-[60]"
              onClick={() => setShowMenu(false)}
            />
            <div
              className={`absolute ${
                isOwn ? 'right-full mr-2' : 'left-full ml-2'
              } bottom-2 z-[70] bg-surface rounded-xl py-1.5 min-w-[150px] animate-scale-in`}
            >
              <button
                onClick={handleCopy}
                className="w-full px-4 py-2 text-left text-xs font-bold flex items-center gap-2.5 hover:bg-surface-hover text-content transition-colors"
              >
                <Copy size={16} /> Sao chép
              </button>
              {isOwn ? (
                <button
                  onClick={() => {
                    onDelete(message._id || message.id);
                    setShowMenu(false);
                  }}
                  className="w-full px-4 py-2 text-left text-xs font-bold flex items-center gap-2.5 hover:bg-error/5 text-error transition-colors"
                >
                  <Trash2 size={16} /> Gỡ bỏ
                </button>
              ) : (
                <button
                  onClick={() => {
                    onReport(message._id || message.id);
                    setShowMenu(false);
                  }}
                  className="w-full px-4 py-2 text-left text-xs font-bold flex items-center gap-2.5 hover:bg-warning/5 text-warning transition-colors"
                >
                  <Flag size={16} /> Báo cáo
                </button>
              )}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

// --- Main Component ---

const MessageDetail = () => {
  const { conversationId } = useParams();
  const navigate = useNavigate();
  const { socket, isConnected, joinRoom, leaveRoom, isUserOnline } =
    useSocketContext() || {};

  const { user: currentUser } = useSelector(state => state.auth);

  // Queries
  const { data: conversationsData } = useConversations();
  const conversationsList =
    conversationsData?.conversations || conversationsData || [];

  const { data: conversationData, isLoading: conversationLoading } =
    useConversationById(conversationId);

  const { data: messagesData, isLoading: messagesLoading } = useMessages({
    conversationId,
    page: 1,
    limit: 50,
  });
  const messagesList = messagesData?.messages || messagesData || [];

  // Mutations
  const markAsReadMutation = useMarkAsRead();
  const sendMessageMutation = useSendMessage();
  const deleteMessageMutation = useDeleteMessage();
  const updateGroupMutation = useUpdateGroup();
  const addMemberMutation = useAddGroupMember();
  const removeMemberMutation = useRemoveGroupMember();
  const leaveGroupMutation = useLeaveGroup();

  const [messageText, setMessageText] = useState('');
  const [selectedImages, setSelectedImages] = useState([]);
  const [showGroupInfo, setShowGroupInfo] = useState(false);
  const [reportMessageId, setReportMessageId] = useState(null);
  const [isOtherUserTyping, setIsOtherUserTyping] = useState(false);
  const [isUpdatingGroup, setIsUpdatingGroup] = useState(false);

  const messagesEndRef = useRef(null);
  const fileInputRef = useRef(null);
  const typingTimeoutRef = useRef(null);

  // Derive conversation
  const conversation = useMemo(() => {
    if (conversationData) return conversationData;
    return conversationsList.find(
      c =>
        c._id === conversationId ||
        c.conversationId === conversationId ||
        c.id === conversationId ||
        c.directId === conversationId
    );
  }, [conversationData, conversationsList, conversationId]);

  const messages = messagesList;

  const otherUser = useMemo(() => {
    if (!conversation || conversation.isGroup) return null;

    // Explicitly find the person who is NOT the current user
    const other =
      conversation.otherUser ||
      conversation.participant ||
      conversation.members?.find(
        m => (m._id || m.id)?.toString() !== currentUser?._id?.toString()
      );

    return other;
  }, [conversation, currentUser]);

  const chatName = conversation?.isGroup
    ? conversation.name || 'Nhóm chat'
    : otherUser?.name ||
      otherUser?.fullName ||
      otherUser?.username ||
      'Đang tải...';
  const chatAvatar = conversation?.isGroup
    ? `https://ui-avatars.com/api/?name=${encodeURIComponent(
        conversation.name || 'G'
      )}&background=random&size=100`
    : otherUser?.avatar || 'https://via.placeholder.com/150';

  const isOnline = otherUser?._id ? isUserOnline?.(otherUser._id) : false;

  // Lifecycle - Join room and fetch messages (only when conversationId changes)
  useEffect(() => {
    if (conversationId) {
      if (joinRoom) joinRoom(conversationId);
      markAsReadMutation.mutate(conversationId);
    }
    return () => {
      if (conversationId && leaveRoom) leaveRoom(conversationId);
    };
  }, [conversationId, joinRoom, leaveRoom]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, isOtherUserTyping]);

  // Socket listeners
  useEffect(() => {
    if (!socket || !isConnected) return;

    const isCurrentConversation = id => {
      return (
        id === conversationId ||
        (conversation &&
          (id === conversation?._id || id === conversation?.directId))
      );
    };

    const handleTyping = data => {
      if (
        isCurrentConversation(data.conversationId) &&
        data.senderId !== currentUser?._id
      ) {
        setIsOtherUserTyping(true);
      }
    };

    const handleStopTyping = data => {
      if (
        isCurrentConversation(data.conversationId) &&
        data.senderId !== currentUser?._id
      ) {
        setIsOtherUserTyping(false);
      }
    };

    socket.on('user_typing', handleTyping);
    socket.on('user_stop_typing', handleStopTyping);
    return () => {
      socket.off('user_typing', handleTyping);
      socket.off('user_stop_typing', handleStopTyping);
    };
  }, [socket, isConnected, conversationId, currentUser?._id, conversation]);

  // Handlers
  const emitTyping = useCallback(
    isTyping => {
      if (!socket || !isConnected || !conversationId) return;
      socket.emit(isTyping ? 'user_typing' : 'user_stop_typing', {
        senderId: currentUser?._id,
        conversationId,
      });
    },
    [socket, isConnected, conversationId, currentUser?._id]
  );

  const handleInputChange = e => {
    setMessageText(e.target.value);
    emitTyping(true);
    if (typingTimeoutRef.current) clearTimeout(typingTimeoutRef.current);
    typingTimeoutRef.current = setTimeout(() => emitTyping(false), 2000);
  };

  const handleSend = async () => {
    if (!messageText.trim() && selectedImages.length === 0) return;
    emitTyping(false);
    try {
      await sendMessageMutation.mutateAsync({
        conversationId,
        content: messageText.trim(),
        type: selectedImages.length > 0 ? 'image' : 'text',
        attachments: selectedImages.length > 0 ? selectedImages : undefined,
      });
      setMessageText('');
      setSelectedImages([]);
    } catch (error) {
      toast.error(error?.response?.data?.message || 'Gửi tin nhắn thất bại');
    }
  };

  const handleDelete = async mid => {
    try {
      await deleteMessageMutation.mutateAsync({
        conversationId,
        messageId: mid,
      });
      toast.success('Đã xóa tin nhắn');
    } catch (err) {
      toast.error(err?.response?.data?.message || 'Không thể xóa tin nhắn');
    }
  };

  const handleGroupRename = async name => {
    try {
      setIsUpdatingGroup(true);
      await updateGroupMutation.mutateAsync({
        groupId: conversationId,
        data: { name },
      });
      toast.success('Đã đổi tên nhóm');
    } catch (err) {
      toast.error(err?.response?.data?.message || 'Cập nhật thất bại');
    } finally {
      setIsUpdatingGroup(false);
    }
  };

  const handleAddMember = async uid => {
    try {
      await addMemberMutation.mutateAsync({
        groupId: conversationId,
        memberIds: [uid],
      });
      toast.success('Đã thêm thành viên');
    } catch (err) {
      toast.error(err?.response?.data?.message || 'Thêm thành viên thất bại');
    }
  };

  const handleRemoveMember = async uid => {
    try {
      await removeMemberMutation.mutateAsync({
        groupId: conversationId,
        userId: uid,
      });
      toast.success('Đã mời thành viên rời nhóm');
    } catch (err) {
      toast.error(err?.response?.data?.message || 'Thực hiện thất bại');
    }
  };

  const handleLeave = async () => {
    if (window.confirm('Bạn có chắc muốn rời nhóm này?')) {
      try {
        await leaveGroupMutation.mutateAsync(conversationId);
        navigate('/messages');
      } catch (err) {
        toast.error(err?.response?.data?.message || 'Rời nhóm thất bại');
      }
    }
  };

  if (!conversation && conversationLoading) {
    return (
      <div className="flex-1 flex flex-col items-center justify-center space-y-4 bg-white dark:bg-neutral-950">
        <Loader2 className="w-10 h-10 animate-spin text-blue-500" />
        <p className="text-sm font-bold text-neutral-400 uppercase tracking-widest">
          Đang tải cuộc trò chuyện...
        </p>
      </div>
    );
  }

  const isLoading = messagesLoading || conversationLoading;

  return (
    <div className="h-full flex flex-col bg-background animate-fade-in">
      {/* Header */}
      <div className="flex items-center justify-between p-4 px-6 bg-surface/80 backdrop-blur-xl z-20 shadow-sm">
        <div className="flex items-center gap-4">
          <button
            onClick={() => navigate('/messages')}
            className="md:hidden p-2 -ml-2 rounded-full hover:bg-surface-hover transition-colors"
          >
            <ArrowLeft size={20} className="text-secondary" />
          </button>

          <div
            className="relative group cursor-pointer"
            onClick={() => conversation?.isGroup && setShowGroupInfo(true)}
          >
            <img
              src={chatAvatar}
              alt=""
              className="w-11 h-11 rounded-full object-cover group-hover:scale-105 transition-transform ring-2 ring-transparent group-hover:ring-primary/20"
            />
            {!conversation?.isGroup && isOnline && (
              <div className="absolute bottom-0 right-0 w-3.5 h-3.5 bg-success rounded-full" />
            )}
          </div>

          <div
            className="min-w-0 flex flex-col cursor-pointer"
            onClick={() => conversation?.isGroup && setShowGroupInfo(true)}
          >
            <h2 className="font-bold text-content truncate text-base tracking-tight leading-tight">
              {chatName}
            </h2>
            <div className="flex items-center gap-1.5">
              {conversation?.isGroup ? (
                <span className="text-[10px] font-black text-text-tertiary uppercase tracking-tight">
                  {conversation.members?.length || 0} thành viên
                </span>
              ) : (
                <span
                  className={`text-[10px] font-black uppercase tracking-tight ${
                    isOnline ? 'text-success' : 'text-text-tertiary'
                  }`}
                >
                  {isOnline ? 'Đang hoạt động' : 'Ngoại tuyến'}
                </span>
              )}
            </div>
          </div>
        </div>

        <div className="flex items-center gap-1">
          <button className="yb-btn-ghost p-2.5 rounded-full transition-all text-secondary hover:text-primary">
            <Phone size={20} />
          </button>
          <button className="yb-btn-ghost p-2.5 rounded-full transition-all text-secondary hover:text-primary">
            <Video size={20} />
          </button>
          <button
            onClick={() => setShowGroupInfo(true)}
            className="yb-btn-ghost p-2.5 rounded-full transition-all text-secondary hover:text-primary"
          >
            <Info size={22} />
          </button>
        </div>
      </div>

      {/* Messages Area */}
      <div className="flex-1 overflow-y-auto p-4 px-6 space-y-4 scroll-smooth custom-scrollbar relative bg-surface-secondary/30">
        {isLoading && messages.length === 0 ? (
          <div className="absolute inset-0 flex flex-col items-center justify-center space-y-3 bg-surface/50 backdrop-blur-[2px] z-10">
            <Loader2 className="w-8 h-8 animate-spin text-primary" />
            <p className="text-[10px] font-bold text-text-tertiary uppercase tracking-widest">
              Đang tải tin nhắn...
            </p>
          </div>
        ) : null}

        {messages.length === 0 && !isLoading ? (
          <div className="flex flex-col items-center justify-center h-full space-y-6">
            <div className="w-20 h-20 rounded-full bg-surface-hover flex items-center justify-center">
              <MessageCircle size={32} className="text-text-tertiary" />
            </div>
            <div className="text-center">
              <p className="font-bold text-content">Chưa có tin nhắn nào</p>
              <p className="text-xs text-text-tertiary font-medium">
                Hãy gửi lời chào đến {chatName}!
              </p>
            </div>
          </div>
        ) : (
          <div className="flex flex-col gap-1.5 min-h-full justify-end">
            {messages.map((msg, idx) => {
              const isOwn =
                msg.isMine ??
                (msg.sender?._id === currentUser?._id ||
                  msg.sender === currentUser?._id);
              const prevMsg = messages[idx - 1];
              const showAvatar =
                !isOwn &&
                (!prevMsg ||
                  (prevMsg.sender?._id || prevMsg.sender) !==
                    (msg.sender?._id || msg.sender));

              const sender = conversation?.isGroup
                ? conversation.members?.find(
                    m => m._id === (msg.sender?._id || msg.sender)
                  )
                : otherUser;

              return (
                <div key={msg._id || msg.id} className="w-full">
                  {conversation?.isGroup && !isOwn && showAvatar && (
                    <span className="text-[10px] font-bold text-text-tertiary ml-10 mb-1 block uppercase tracking-tight">
                      {sender?.name || 'Thành viên'}
                    </span>
                  )}
                  <MessageBubble
                    message={msg}
                    isOwn={isOwn}
                    showAvatar={showAvatar}
                    avatar={sender?.avatar}
                    onDelete={handleDelete}
                    onReport={id => setReportMessageId(id)}
                  />
                </div>
              );
            })}
          </div>
        )}
        {isOtherUserTyping && (
          <TypingIndicator
            username={conversation?.isGroup ? 'Ai đó' : chatName}
          />
        )}
        <div ref={messagesEndRef} className="h-2" />
      </div>

      {/* Input Area */}
      <div className="p-4 px-6 bg-surface">
        {selectedImages.length > 0 && (
          <div className="flex gap-3 overflow-x-auto pb-4 hide-scrollbar">
            {selectedImages.map((file, i) => (
              <div
                key={i}
                className="relative group flex-shrink-0 animate-scale-in"
              >
                <img
                  src={URL.createObjectURL(file)}
                  alt=""
                  className="w-20 h-20 rounded-xl object-cover"
                />
                <button
                  onClick={() =>
                    setSelectedImages(prev =>
                      prev.filter((_, idx) => idx !== i)
                    )
                  }
                  className="absolute -top-2 -right-2 bg-primary text-primary-foreground rounded-full p-1 hover:bg-error transition-colors"
                >
                  <X size={12} />
                </button>
              </div>
            ))}
          </div>
        )}

        <div className="flex items-center gap-3">
          <input
            ref={fileInputRef}
            type="file"
            multiple
            accept="image/*"
            className="hidden"
            onChange={e =>
              setSelectedImages(prev => [
                ...prev,
                ...Array.from(e.target.files),
              ])
            }
          />
          <div className="flex items-center bg-surface-secondary rounded-2xl flex-1 px-1 focus-within:bg-white transition-all duration-300">
            <button
              onClick={() => fileInputRef.current?.click()}
              className="yb-btn-ghost p-3 text-secondary hover:text-primary rounded-full transition-all"
            >
              <ImageIcon size={20} />
            </button>
            <button className="yb-btn-ghost p-3 text-secondary hover:text-primary rounded-full transition-all">
              <Smile size={20} />
            </button>
            <input
              type="text"
              value={messageText}
              onChange={handleInputChange}
              onKeyDown={e => e.key === 'Enter' && !e.shiftKey && handleSend()}
              placeholder="Nhập tin nhắn..."
              className="flex-1 bg-transparent border-none py-4 px-2 text-sm focus:outline-none text-content placeholder:text-text-tertiary font-medium"
            />
          </div>
          <button
            onClick={handleSend}
            disabled={
              (!messageText.trim() && selectedImages.length === 0) ||
              sendMessageMutation.isPending
            }
            className={`yb-btn p-4 rounded-full transition-all duration-300 ${
              (messageText.trim() || selectedImages.length > 0) &&
              !sendMessageMutation.isPending
                ? 'yb-btn-primary hover:scale-105 active:scale-95'
                : 'bg-surface-secondary text-text-tertiary cursor-not-allowed'
            }`}
          >
            {sendMessageMutation.isPending ? (
              <Loader2 size={24} className="animate-spin" />
            ) : (
              <Send
                size={24}
                className={
                  messageText.trim() ? 'translate-x-0.5 -translate-y-0.5' : ''
                }
              />
            )}
          </button>
        </div>
      </div>

      {/* Modals */}
      <Suspense fallback={null}>
        {showGroupInfo && (
          <GroupInfoModal
            isOpen={showGroupInfo}
            onClose={() => setShowGroupInfo(false)}
            conversation={conversation}
            currentUserId={currentUser?._id}
            onRename={handleRename => handleGroupRename(handleRename)}
            onAddMember={uid => handleAddMember(uid)}
            onRemoveMember={uid => handleRemoveMember(uid)}
            onLeaveGroup={handleLeave}
            isUpdating={isUpdatingGroup}
          />
        )}
        {reportMessageId && (
          <ReportModal
            isOpen={!!reportMessageId}
            onClose={() => setReportMessageId(null)}
            targetId={reportMessageId}
            targetType="message"
          />
        )}
      </Suspense>
    </div>
  );
};

