import { useState, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { Search, X, UserPlus, Check, Loader2 } from 'lucide-react';
import { useSearchUsers } from '@/hooks/useSearchQuery';
import { useFollowUser, useUnfollowUser } from '@/hooks/useUserQuery';
import toast from 'react-hot-toast';

const SearchUser = ({ isOpen, onClose }) => {
  const { user: currentUser } = useSelector(state => state.auth);

  const [searchQuery, setSearchQuery] = useState('');
  const searchInputRef = useRef(null);
  const modalRef = useRef(null);
  const [debouncedSearch, setDebouncedSearch] = useState('');

  const followMutation = useFollowUser();
  const unfollowMutation = useUnfollowUser();

  useEffect(() => {
    if (isOpen && searchInputRef.current) {
      searchInputRef.current.focus();
    }
  }, [isOpen]);

  useEffect(() => {
    const handleClickOutside = event => {
      if (modalRef.current && !modalRef.current.contains(event.target)) {
        onClose();
      }
    };

    if (isOpen) {
      document.addEventListener('mousedown', handleClickOutside);
    }
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isOpen, onClose]);

  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedSearch(searchQuery);
    }, 300);
    return () => clearTimeout(timer);
  }, [searchQuery]);

  const { data: searchResults, isLoading: loading } = useSearchUsers({
    query: debouncedSearch,
    page: 1,
    limit: 10,
  });

  const handleFollow = useCallback(
    async (e, userId, isCurrentlyFollowing) => {
      e.preventDefault();
      e.stopPropagation();

      try {
        if (isCurrentlyFollowing) {
          await unfollowMutation.mutateAsync(userId);
          toast.success('Đã bỏ theo dõi');
        } else {
          await followMutation.mutateAsync(userId);
          toast.success('Đã theo dõi');
        }
      } catch (error) {
        toast.error(error?.response?.data?.message || 'Thao tác thất bại');
      }
    },
    [followMutation, unfollowMutation]
  );

  const handleClearSearch = () => {
    setSearchQuery('');
  };

  const users =
    searchResults?.users || searchResults?.data || searchResults || [];

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 z-[60] flex items-start justify-center bg-black/50 backdrop-blur-sm pt-[10vh]">
      <div
        ref={modalRef}
        className="w-full max-w-[600px] bg-white dark:bg-neutral-900 rounded-2xl shadow-2xl flex flex-col max-h-[70vh] overflow-hidden mx-4"
      >
        {/* Search Header */}
        <div className="p-4 border-b border-neutral-200 dark:border-neutral-800">
          <div className="relative">
            <Search
              size={18}
              className="absolute left-3 top-1/2 -translate-y-1/2 text-neutral-400"
            />
            <input
              ref={searchInputRef}
              type="text"
              value={searchQuery}
              onChange={e => setSearchQuery(e.target.value)}
              placeholder="Search users..."
              className="w-full pl-10 pr-10 py-3 rounded-full bg-neutral-100 dark:bg-neutral-800 text-black dark:text-white placeholder:text-neutral-400 text-sm focus:outline-none focus:ring-2 focus:ring-black dark:focus:ring-white"
            />
            {searchQuery && (
              <button
                onClick={handleClearSearch}
                className="absolute right-3 top-1/2 -translate-y-1/2 p-1 rounded-full hover:bg-neutral-200 dark:hover:bg-neutral-700"
              >
                <X size={16} className="text-neutral-400" />
              </button>
            )}
          </div>
        </div>

        {/* Search Results */}
        <div className="flex-1 overflow-y-auto">
          {loading ? (
            <div className="p-8 text-center">
              <Loader2 className="w-6 h-6 animate-spin mx-auto text-neutral-400" />
            </div>
          ) : users.length === 0 ? (
            <div className="p-8 text-center">
              {searchQuery ? (
                <>
                  <Search size={32} className="mx-auto text-neutral-300 mb-2" />
                  <p className="text-neutral-500 text-sm">No users found</p>
                </>
              ) : (
                <>
                  <Search size={32} className="mx-auto text-neutral-300 mb-2" />
                  <p className="text-neutral-500 text-sm">
                    Search for users by name or username
                  </p>
                </>
              )}
            </div>
          ) : (
            users.map(user => {
              const isFollowed = user.isFollowing;
              const isMutationLoading =
                (followMutation.isPending &&
                  followMutation.variables === user._id) ||
                (unfollowMutation.isPending &&
                  unfollowMutation.variables === user._id);
              const isSelf = currentUser?._id === user._id;

              return (
                <Link
                  key={user._id}
                  to={`/profile/${user._id}`}
                  onClick={onClose}
                  className="flex items-center gap-3 p-4 hover:bg-neutral-50 dark:hover:bg-neutral-800/50 transition-colors"
                >
                  {/* Avatar */}
                  <img
                    src={
                      user.avatar ||
                      `https://api.dicebear.com/7.x/avataaars/svg?seed=${user.username}`
                    }
                    alt={user.name || user.username}
                    className="w-12 h-12 rounded-full object-cover border-2 border-neutral-200 dark:border-neutral-700"
                  />

                  {/* Info */}
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center gap-2">
                      <span className="font-medium text-black dark:text-white truncate">
                        {user.name || user.fullName || user.username}
                      </span>
                    </div>
                    <p className="text-sm text-neutral-500 truncate">
                      @{user.username}
                    </p>
                    {user.bio && (
                      <p className="text-xs text-neutral-400 truncate mt-0.5">
                        {user.bio}
                      </p>
                    )}
                  </div>

                  {/* Follow Button - Don't show for self */}
                  {!isSelf && (
                    <button
                      onClick={e => handleFollow(e, user._id, isFollowed)}
                      disabled={isMutationLoading}
                      className={`flex items-center gap-1.5 px-3 py-1.5 rounded-full text-xs font-medium transition-colors ${
                        isMutationLoading ? 'opacity-50 cursor-not-allowed' : ''
                      } ${
                        isFollowed
                          ? 'bg-neutral-100 dark:bg-neutral-800 text-black dark:text-white border border-neutral-200 dark:border-neutral-700'
                          : 'bg-primary text-primary-foreground'
                      }`}
                    >
                      {isMutationLoading ? (
                        <Loader2 size={14} className="animate-spin" />
                      ) : isFollowed ? (
                        <>
                          <Check size={14} />
                          Following
                        </>
                      ) : (
                        <>
                          <UserPlus size={14} />
                          Follow
                        </>
                      )}
                    </button>
                  )}
                </Link>
              );
            })
          )}
        </div>

        {/* Close Button */}
        <div className="p-4 border-t border-neutral-200 dark:border-neutral-800">
          <button
            onClick={onClose}
            className="w-full py-2.5 rounded-full bg-neutral-100 dark:bg-neutral-800 text-black dark:text-white text-sm font-medium hover:bg-neutral-200 dark:hover:bg-neutral-700 transition-colors"
          >
            Close
          </button>
        </div>
      </div>
    </div>
  );
};

export default SearchUser;

import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import {
  User,
  Mail,
  Lock,
  Shield,
  Trash2,
  LogOut,
  Monitor,
} from 'lucide-react';
import { logout, logoutAll, updatePassword } from '@/redux/actions/authActions';
import toast from 'react-hot-toast';

const AccountSettings = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { user: authUser } = useSelector(state => state.auth);

  // Use authUser or empty object
  const profile = authUser || {};

  const [account, setAccount] = useState({
    email: 'johndoe@example.com',
    username: 'johndoe',
    phone: '',
  });

  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [showPasswordModal, setShowPasswordModal] = useState(false);
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: '',
  });
  const [loading, setLoading] = useState(false);

  const handleLogout = async () => {
    const result = await dispatch(logout());
    if (logout.fulfilled.match(result)) {
      toast.success('Đăng xuất thành công');
      navigate('/auth/login');
    }
  };

  const handleLogoutAll = async () => {
    const result = await dispatch(logoutAll());
    if (logoutAll.fulfilled.match(result)) {
      toast.success('Đã đăng xuất khỏi tất cả thiết bị');
      navigate('/auth/login');
    }
  };

  const handleUpdatePassword = async () => {
    if (!passwordData.currentPassword || !passwordData.newPassword) {
      toast.error('Vui lòng điền đầy đủ thông tin');
      return;
    }
    if (passwordData.newPassword.length < 6) {
      toast.error('Mật khẩu mới phải có ít nhất 6 ký tự');
      return;
    }
    if (passwordData.newPassword !== passwordData.confirmPassword) {
      toast.error('Mật khẩu xác nhận không khớp');
      return;
    }

    setLoading(true);
    const result = await dispatch(
      updatePassword({
        currentPassword: passwordData.currentPassword,
        newPassword: passwordData.newPassword,
      })
    );
    setLoading(false);

    if (updatePassword.fulfilled.match(result)) {
      toast.success('Đổi mật khẩu thành công. Vui lòng đăng nhập lại');
      setShowPasswordModal(false);
      setPasswordData({
        currentPassword: '',
        newPassword: '',
        confirmPassword: '',
      });
      navigate('/auth/login');
    }
  };

  const InputField = ({
    icon: Icon,
    label,
    type = 'text',
    value,
    onChange,
    placeholder,
  }) => (
    <div className="space-y-2">
      <label className="text-sm font-medium text-content dark:text-white">
        {label}
      </label>
      <div className="relative">
        <Icon
          size={18}
          className="absolute left-3 top-1/2 -translate-y-1/2 text-neutral-400"
        />
        <input
          type={type}
          value={value}
          onChange={onChange}
          placeholder={placeholder}
          className="w-full pl-10 pr-4 py-2.5 rounded-xl bg-neutral-100 dark:bg-neutral-800 text-content dark:text-white placeholder:text-neutral-400 text-sm focus:outline-none focus:ring-2 focus:ring-primary/50"
        />
      </div>
    </div>
  );

  return (
    <div className="space-y-8">
      <div>
        <h1 className="text-2xl font-bold text-content dark:text-white mb-2">
          Account
        </h1>
        <p className="text-neutral-500 text-sm">
          Manage your account information and security
        </p>
      </div>

      {/* Account Info */}
      <div className="rounded-2xl overflow-hidden bg-neutral-50/50 dark:bg-neutral-800/20">
        <div className="px-4 py-3 bg-neutral-100/50 dark:bg-neutral-700/30">
          <div className="flex items-center gap-2">
            <User size={16} className="text-neutral-500" />
            <h3 className="text-sm font-medium text-content dark:text-white">
              Account Information
            </h3>
          </div>
        </div>
        <div className="p-4 space-y-4">
          <InputField
            icon={User}
            label="Username"
            value={profile.username || ''}
            onChange={e => setAccount({ ...account, username: e.target.value })}
            placeholder="Enter username"
          />
          <InputField
            icon={Mail}
            label="Email"
            type="email"
            value={profile.email || ''}
            onChange={e => setAccount({ ...account, email: e.target.value })}
            placeholder="Enter email"
          />
        </div>
      </div>

      {/* Security */}
      <div className="rounded-2xl overflow-hidden bg-neutral-50/50 dark:bg-neutral-800/20">
        <div className="px-4 py-3 bg-neutral-100/50 dark:bg-neutral-700/30">
          <div className="flex items-center gap-2">
            <Shield size={16} className="text-neutral-500" />
            <h3 className="text-sm font-medium text-content dark:text-white">
              Security
            </h3>
          </div>
        </div>
        <div className="p-4 space-y-4">
          <button
            onClick={() => setShowPasswordModal(true)}
            className="w-full flex items-center justify-between p-3 rounded-xl hover:bg-neutral-50 dark:hover:bg-neutral-800 transition-colors"
          >
            <div className="flex items-center gap-3">
              <Lock size={18} className="text-neutral-500" />
              <div className="text-left">
                <p className="text-sm font-medium text-content dark:text-white">
                  Change Password
                </p>
                <p className="text-xs text-neutral-500">
                  Update your password regularly for security
                </p>
              </div>
            </div>
            <span className="text-xs text-neutral-400">→</span>
          </button>

          <button className="w-full flex items-center justify-between p-3 rounded-xl hover:bg-neutral-50 dark:hover:bg-neutral-800 transition-colors">
            <div className="flex items-center gap-3">
              <Shield size={18} className="text-neutral-500" />
              <div className="text-left">
                <p className="text-sm font-medium text-content dark:text-white">
                  Two-Factor Authentication
                </p>
                <p className="text-xs text-neutral-500">
                  Add an extra layer of security
                </p>
              </div>
            </div>
            <span className="text-xs px-2 py-0.5 bg-neutral-100 dark:bg-neutral-800 rounded-full text-neutral-500">
              Off
            </span>
          </button>
        </div>
      </div>

      {/* Session Management */}
      <div className="rounded-2xl overflow-hidden bg-neutral-50/50 dark:bg-neutral-800/20">
        <div className="px-4 py-3 bg-neutral-100/50 dark:bg-neutral-700/30">
          <div className="flex items-center gap-2">
            <Monitor size={16} className="text-neutral-500" />
            <h3 className="text-sm font-medium text-content dark:text-white">
              Sessions
            </h3>
          </div>
        </div>
        <div className="p-4 space-y-4">
          <button
            onClick={handleLogout}
            className="w-full flex items-center justify-between p-3 rounded-xl hover:bg-neutral-50 dark:hover:bg-neutral-800 transition-colors"
          >
            <div className="flex items-center gap-3">
              <LogOut size={18} className="text-neutral-500" />
              <div className="text-left">
                <p className="text-sm font-medium text-content dark:text-white">
                  Logout
                </p>
                <p className="text-xs text-neutral-500">
                  Sign out from this device
                </p>
              </div>
            </div>
            <span className="text-xs text-neutral-400">→</span>
          </button>

          <button
            onClick={handleLogoutAll}
            className="w-full flex items-center justify-between p-3 rounded-xl hover:bg-neutral-50 dark:hover:bg-neutral-800 transition-colors"
          >
            <div className="flex items-center gap-3">
              <Monitor size={18} className="text-red-500" />
              <div className="text-left">
                <p className="text-sm font-medium text-content dark:text-white">
                  Logout from all devices
                </p>
                <p className="text-xs text-neutral-500">
                  Sign out from all your active sessions
                </p>
              </div>
            </div>
            <span className="text-xs text-neutral-400">→</span>
          </button>
        </div>
      </div>

      {/* Danger Zone */}
      <div className="rounded-2xl overflow-hidden bg-red-50/30 dark:bg-red-900/10">
        <div className="px-4 py-3 bg-red-50/50 dark:bg-red-900/20">
          <div className="flex items-center gap-2">
            <Trash2 size={16} className="text-red-500" />
            <h3 className="text-sm font-medium text-red-600 dark:text-red-400">
              Danger Zone
            </h3>
          </div>
        </div>
        <div className="p-4">
          <button
            onClick={() => setShowDeleteModal(true)}
            className="w-full py-2.5 rounded-xl bg-red-100 dark:bg-red-900/30 text-red-600 dark:text-red-400 text-sm font-medium hover:bg-red-200 dark:hover:bg-red-900/50 transition-colors"
          >
            Delete Account
          </button>
          <p className="text-xs text-neutral-500 mt-2 text-center">
            This action cannot be undone. All your data will be permanently
            deleted.
          </p>
        </div>
      </div>

      {/* Delete Modal */}
      {showDeleteModal && (
        <div
          className="fixed inset-0 z-50 flex items-center justify-center bg-black/60 backdrop-blur-sm"
          onClick={() => setShowDeleteModal(false)}
        >
          <div
            className="w-full max-w-sm mx-4 bg-white dark:bg-neutral-900 rounded-2xl p-6"
            onClick={e => e.stopPropagation()}
          >
            <h3 className="text-lg font-bold text-content dark:text-white mb-2">
              Delete Account?
            </h3>
            <p className="text-sm text-neutral-500 mb-6">
              Are you sure you want to delete your account? This action cannot
              be undone.
            </p>
            <div className="flex gap-3">
              <button
                onClick={() => setShowDeleteModal(false)}
                className="flex-1 py-2.5 rounded-full bg-neutral-100 dark:bg-neutral-800 text-content dark:text-white text-sm font-medium hover:bg-neutral-200 dark:hover:bg-neutral-700 transition-colors"
              >
                Cancel
              </button>
              <button className="flex-1 py-2.5 rounded-full bg-red-500 text-white text-sm font-medium hover:bg-red-600 transition-colors">
                Delete
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Password Change Modal */}
      {showPasswordModal && (
        <div
          className="fixed inset-0 z-50 flex items-center justify-center bg-black/60 backdrop-blur-sm"
          onClick={() => setShowPasswordModal(false)}
        >
          <div
            className="w-full max-w-md mx-4 bg-white dark:bg-neutral-900 rounded-2xl p-6"
            onClick={e => e.stopPropagation()}
          >
            <h3 className="text-lg font-bold text-content dark:text-white mb-4">
              Đổi mật khẩu
            </h3>
            <div className="space-y-4">
              <div className="space-y-2">
                <label className="text-sm font-medium text-content dark:text-white">
                  Mật khẩu hiện tại
                </label>
                <input
                  type="password"
                  value={passwordData.currentPassword}
                  onChange={e =>
                    setPasswordData({
                      ...passwordData,
                      currentPassword: e.target.value,
                    })
                  }
                  placeholder="Nhập mật khẩu hiện tại"
                  className="w-full px-4 py-2.5 rounded-xl bg-neutral-100 dark:bg-neutral-800 text-content dark:text-white placeholder:text-neutral-400 text-sm focus:outline-none focus:ring-2 focus:ring-primary/50"
                />
              </div>
              <div className="space-y-2">
                <label className="text-sm font-medium text-content dark:text-white">
                  Mật khẩu mới
                </label>
                <input
                  type="password"
                  value={passwordData.newPassword}
                  onChange={e =>
                    setPasswordData({
                      ...passwordData,
                      newPassword: e.target.value,
                    })
                  }
                  placeholder="Nhập mật khẩu mới (ít nhất 6 ký tự)"
                  className="w-full px-4 py-2.5 rounded-xl bg-neutral-100 dark:bg-neutral-800 text-content dark:text-white placeholder:text-neutral-400 text-sm focus:outline-none focus:ring-2 focus:ring-primary/50"
                />
              </div>
              <div className="space-y-2">
                <label className="text-sm font-medium text-content dark:text-white">
                  Xác nhận mật khẩu mới
                </label>
                <input
                  type="password"
                  value={passwordData.confirmPassword}
                  onChange={e =>
                    setPasswordData({
                      ...passwordData,
                      confirmPassword: e.target.value,
                    })
                  }
                  placeholder="Nhập lại mật khẩu mới"
                  className="w-full px-4 py-2.5 rounded-xl bg-neutral-100 dark:bg-neutral-800 text-content dark:text-white placeholder:text-neutral-400 text-sm focus:outline-none focus:ring-2 focus:ring-primary/50"
                />
              </div>
            </div>
            <div className="flex gap-3 mt-6">
              <button
                onClick={() => {
                  setShowPasswordModal(false);
                  setPasswordData({
                    currentPassword: '',
                    newPassword: '',
                    confirmPassword: '',
                  });
                }}
                className="flex-1 py-2.5 rounded-full bg-neutral-100 dark:bg-neutral-800 text-content dark:text-white text-sm font-medium hover:bg-neutral-200 dark:hover:bg-neutral-700 transition-colors"
              >
                Hủy
              </button>
              <button
                onClick={handleUpdatePassword}
                disabled={loading}
                className="flex-1 py-2.5 rounded-full bg-primary text-primary-foreground text-sm font-medium hover:opacity-90 transition-opacity disabled:opacity-50 flex items-center justify-center"
              >
                {loading ? (
                  <div className="w-5 h-5 border-2 border-white/30 dark:border-black/30 border-t-white dark:border-t-black rounded-full animate-spin" />
                ) : (
                  'Xác nhận'
                )}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AccountSettings;

import { useState, useEffect } from 'react';
import {
  Bell,
  Heart,
  MessageCircle,
  UserPlus,
  AtSign,
  Mail,
  Loader2,
} from 'lucide-react';
import toast from 'react-hot-toast';
import { useSettings, useUpdateSettings } from '@/hooks/useUserQuery';
import LoadingSpinner from '@/components/Common/LoadingSpinner';

const NotificationSettings = () => {
  const { data: settingsData, isLoading: settingsLoading } = useSettings();
  const updateSettingsMutation = useUpdateSettings();

  const [notifications, setNotifications] = useState({
    likes: true,
    comments: true,
    follows: true,
    mentions: true,
    messages: true,
    email: false,
    push: true,
  });

  // Sync local state with server settings
  useEffect(() => {
    if (settingsData?.notifications) {
      const apiSettings = { ...settingsData.notifications };

      // Handle legacy/nested structure where email/push might be objects
      if (typeof apiSettings.email === 'object' && apiSettings.email !== null) {
        apiSettings.email = apiSettings.email.enabled;
      }
      if (typeof apiSettings.push === 'object' && apiSettings.push !== null) {
        apiSettings.push = apiSettings.push.enabled;
      }

      setNotifications(prev => ({
        ...prev,
        ...apiSettings,
      }));
    }
  }, [settingsData]);

  const handleToggle = async settingKey => {
    const newNotifications = {
      ...notifications,
      [settingKey]: !notifications[settingKey],
    };

    setNotifications(newNotifications);

    try {
      await updateSettingsMutation.mutateAsync({
        type: 'notifications',
        settings: newNotifications,
      });
      toast.success('Đã lưu cài đặt thông báo');
    } catch (error) {
      // Revert on failure
      setNotifications(notifications);
      toast.error(error?.response?.data?.message || 'Lưu cài đặt thất bại');
    }
  };

  const ToggleSwitch = ({ enabled, onChange, disabled }) => (
    <button
      onClick={onChange}
      disabled={disabled}
      className={`relative w-11 h-6 rounded-full transition-colors ${
        disabled ? 'opacity-50 cursor-not-allowed' : ''
      } ${enabled ? 'bg-primary' : 'bg-neutral-200 dark:bg-neutral-700'}`}
    >
      <div
        className={`absolute top-0.5 w-5 h-5 rounded-full transition-transform ${
          enabled
            ? 'translate-x-5 bg-primary-foreground'
            : 'translate-x-0.5 bg-white dark:bg-neutral-400'
        }`}
      />
    </button>
  );

  const NotificationItem = ({ icon: Icon, label, description, settingKey }) => (
    <div className="flex items-center justify-between py-4 last:border-0">
      <div className="flex items-center gap-3">
        <div className="w-10 h-10 rounded-full bg-neutral-100 dark:bg-neutral-800 flex items-center justify-center">
          <Icon size={18} className="text-neutral-500" />
        </div>
        <div>
          <p className="text-sm font-medium text-content dark:text-white">
            {label}
          </p>
          <p className="text-xs text-neutral-500">{description}</p>
        </div>
      </div>
      <ToggleSwitch
        enabled={notifications[settingKey]}
        onChange={() => handleToggle(settingKey)}
        disabled={updateSettingsMutation.isPending}
      />
    </div>
  );

  if (settingsLoading && !settingsData) {
    return (
      <div className="flex justify-center items-center py-16">
        <LoadingSpinner size="md" />
      </div>
    );
  }

  return (
    <div className="space-y-8">
      <div>
        <h1 className="text-2xl font-bold text-content dark:text-white mb-2">
          Notifications
        </h1>
        <p className="text-neutral-500 text-sm">
          Choose what notifications you want to receive
        </p>
      </div>

      {/* Activity Notifications */}
      <div className="rounded-2xl overflow-hidden bg-neutral-50/50 dark:bg-neutral-800/20">
        <div className="px-4 py-3 bg-neutral-100/50 dark:bg-neutral-700/30">
          <div className="flex items-center gap-2">
            <Bell size={16} className="text-neutral-500" />
            <h3 className="text-sm font-medium text-content dark:text-white">
              Activity
            </h3>
          </div>
        </div>
        <div className="p-4">
          <NotificationItem
            icon={Heart}
            label="Likes"
            description="When someone likes your post"
            settingKey="likes"
          />
          <NotificationItem
            icon={MessageCircle}
            label="Comments"
            description="When someone comments on your post"
            settingKey="comments"
          />
          <NotificationItem
            icon={UserPlus}
            label="New Followers"
            description="When someone follows you"
            settingKey="follows"
          />
          <NotificationItem
            icon={AtSign}
            label="Mentions"
            description="When someone mentions you"
            settingKey="mentions"
          />
          <NotificationItem
            icon={MessageCircle}
            label="Messages"
            description="When you receive a new message"
            settingKey="messages"
          />
        </div>
      </div>

      {/* Delivery Methods */}
      <div className="rounded-2xl overflow-hidden bg-neutral-50/50 dark:bg-neutral-800/20">
        <div className="px-4 py-3 bg-neutral-100/50 dark:bg-neutral-700/30">
          <div className="flex items-center gap-2">
            <Mail size={16} className="text-neutral-500" />
            <h3 className="text-sm font-medium text-content dark:text-white">
              Delivery Methods
            </h3>
          </div>
        </div>
        <div className="p-4">
          <div className="flex items-center justify-between py-4">
            <div>
              <p className="text-sm font-medium text-content dark:text-white">
                Push Notifications
              </p>
              <p className="text-xs text-neutral-500">
                Receive notifications on your device
              </p>
            </div>
            <ToggleSwitch
              enabled={notifications.push}
              onChange={() => handleToggle('push')}
              disabled={updateSettingsMutation.isPending}
            />
          </div>
          <div className="flex items-center justify-between py-4">
            <div>
              <p className="text-sm font-medium text-content dark:text-white">
                Email Notifications
              </p>
              <p className="text-xs text-neutral-500">
                Receive notifications via email
              </p>
            </div>
            <ToggleSwitch
              enabled={notifications.email}
