import { useEffect, useRef, useState, useCallback, useMemo } from 'react';
import { useQueryClient } from '@tanstack/react-query';
import io from 'socket.io-client';
import { toast } from 'react-hot-toast';
import { SOCKET_URL, MAX_RECONNECT_ATTEMPTS } from '@/constants/socket';

/**
 * Hook to manage Socket.IO connection
 * @param {string} userId - Current user ID
 * @returns {Object} Socket instance and utility functions
 * @returns {Object|null} returns.socket - Socket.IO instance
 * @returns {Function} returns.joinRoom - Join room function
 * @returns {Function} returns.leaveRoom - Leave room function
 * @returns {Function} returns.sendMessage - Send message function
 * @returns {Function} returns.emitEvent - Emit custom event function
 * @returns {Function} returns.joinPostRoom - Join post room function
 * @returns {Function} returns.emitLikeAction - Emit like action function
 * @returns {boolean} returns.isConnected - Connection status
 * @returns {Object} returns.onlineUsers - Online users map
 * @returns {Function} returns.isUserOnline - Check user online function
 */
const useSocket = userId => {
  const socketRef = useRef(null);
  const queryClient = useQueryClient();
  const reconnectAttempts = useRef(0);
  const [isConnected, setIsConnected] = useState(false);
  const activeRooms = useRef(new Set());
  const [onlineUsers, setOnlineUsers] = useState({});

  useEffect(() => {
    if (!userId) return;

    const socket = io(SOCKET_URL, {
      withCredentials: true,
      reconnection: true,
      reconnectionDelay: 1000,
      reconnectionDelayMax: 5000,
      reconnectionAttempts: MAX_RECONNECT_ATTEMPTS,
      path: '/socket.io/',
    });

    socketRef.current = socket;

    socket.on('connect', () => {
      reconnectAttempts.current = 0;
      setIsConnected(true);

      socket.emit('register_user', { userId });
      socket.emit('join_room', userId);
      activeRooms.current.add(userId);
      activeRooms.current.forEach(room => {
        if (room !== userId) socket.emit('join_room', room);
      });

      socket.emit('get_online_users');
      queryClient.invalidateQueries(['messages', 'unreadCount']);
      queryClient.invalidateQueries(['notifications', 'unreadCount']);
    });

    socket.on('disconnect', reason => {
      setIsConnected(false);
      if (reason === 'io server disconnect') {
        setTimeout(() => socket.connect(), 1000);
      }
    });

    socket.on('connect_error', () => {
      setIsConnected(false);
      reconnectAttempts.current++;
      if (reconnectAttempts.current >= MAX_RECONNECT_ATTEMPTS) {
        toast.error('Unable to connect to chat server');
      }
    });

    registerMessageHandlers(socket, userId, queryClient);
    registerNotificationHandlers(socket, queryClient);
    registerLikeHandlers(socket, userId, queryClient);
    registerCommentHandlers(socket, userId, queryClient);
    registerUserStatusHandlers(socket, setOnlineUsers);

    return () => {
      if (socket) {
        socket.disconnect();
        socketRef.current = null;
        setIsConnected(false);
        activeRooms.current.clear();
      }
    };
  }, [userId, queryClient]);

  /**
   * Join a room
   * @param {string} roomId - Room ID
   */
  const joinRoom = useCallback(roomId => {
    if (!roomId) return;
    if (activeRooms.current.has(roomId)) return;

    if (socketRef.current?.connected) {
      socketRef.current.emit('join_room', roomId);
    }
    activeRooms.current.add(roomId);
  }, []);

  /**
   * Leave a room
   * @param {string} roomId - Room ID
   */
  const leaveRoom = useCallback(roomId => {
    if (!roomId) return;
    if (!activeRooms.current.has(roomId)) return;

    activeRooms.current.delete(roomId);
    if (socketRef.current?.connected) {
      socketRef.current.emit('leave_room', roomId);
    }
  }, []);

  /**
   * Send message via socket
   * @param {Object} data - Message data
   * @returns {boolean} Send result
   */
  const sendMessage = useCallback(data => {
    if (!socketRef.current?.connected) return false;
    socketRef.current.emit('send_message', data);
    return true;
  }, []);

  /**
   * Emit custom event
   * @param {string} event - Event name
   * @param {*} data - Event data
   * @returns {boolean} Emit result
   */
  const emitEvent = useCallback((event, data) => {
    if (!socketRef.current?.connected) return false;
    socketRef.current.emit(event, data);
    return true;
  }, []);

  /**
   * Join post room to receive updates
   * @param {string} postId - Post ID
   */
  const joinPostRoom = useCallback(
    postId => {
      if (!postId) return;
      joinRoom(`post:${postId}`);
      emitEvent('post:like:listen', postId);
    },
    [joinRoom, emitEvent]
  );

  /**
   * Emit like action for post
   * @param {string} postId - Post ID
   * @param {string} action - Action ('like' | 'unlike')
   */
  const emitLikeAction = useCallback(
    (postId, action) => {
      emitEvent('post:like', { postId, userId, action });
    },
    [emitEvent, userId]
  );

  /**
   * Check if user is online
   * @param {string} uid - User ID to check
   * @returns {boolean} Online status
   */
  const isUserOnline = useCallback(
    uid => {
      if (!uid) return false;
      return !!onlineUsers[uid.toString()];
    },
    [onlineUsers]
  );

  return useMemo(
    () => ({
      socket: socketRef.current,
      joinRoom,
      leaveRoom,
      sendMessage,
      emitEvent,
      joinPostRoom,
      emitLikeAction,
      isConnected,
      onlineUsers,
      isUserOnline,
    }),
    [
      joinRoom,
      leaveRoom,
      sendMessage,
      emitEvent,
      joinPostRoom,
      emitLikeAction,
      isConnected,
      onlineUsers,
      isUserOnline,
    ]
  );
};

/**
 * Register message handlers
 * @param {Object} socket - Socket instance
 * @param {string} userId - Current user ID
 * @param {Object} queryClient - React Query client
 */
const registerMessageHandlers = (socket, userId, queryClient) => {
  socket.on('new_message', message => {
    if (!message?._id) return;

    const senderId = message.sender?._id || message.sender;
    const isMine =
      senderId && userId && senderId.toString() === userId.toString();

    queryClient.invalidateQueries(['messages', 'list', message.conversationId]);
    queryClient.invalidateQueries([
      'messages',
      'infinite',
      message.conversationId,
    ]);
    queryClient.invalidateQueries(['messages', 'conversations']);
    queryClient.invalidateQueries(['messages', 'unreadCount']);

    if (!isMine) {
      const senderName =
        message.sender?.firstName || message.sender?.name || 'User';
      toast.success(`New message from ${senderName}`);
    }
  });

  socket.on('message_read', data => {
    if (data?.messageId) {
      queryClient.invalidateQueries(['messages']);
    }
  });

  socket.on('user_typing', () => {});
  socket.on('user_stop_typing', () => {});
};

/**
 * Register notification handlers
 * @param {Object} socket - Socket instance
 * @param {Object} queryClient - React Query client
 */
const registerNotificationHandlers = (socket, queryClient) => {
  const handleNotification = notification => {
    if (!notification?._id) {
      queryClient.invalidateQueries(['notifications', 'unreadCount']);
      return;
    }

    queryClient.invalidateQueries(['notifications']);
    queryClient.invalidateQueries(['notifications', 'unreadCount']);

    let msg = notification.content || 'You have a new notification';
    if (notification.type === 'like' && notification.post?.caption) {
      msg += ` - "${notification.post.caption.substring(0, 20)}..."`;
    }
    toast.success(msg, { icon: '🔔' });
  };

  socket.on('notification:new', handleNotification);
  socket.on('new_notification', handleNotification);
};

/**
 * Register like handlers
 * @param {Object} socket - Socket instance
 * @param {string} currentUserId - Current user ID
 * @param {Object} queryClient - React Query client
 */
const registerLikeHandlers = (socket, currentUserId, queryClient) => {
  socket.on('post:like:update', ({ postId, userId }) => {
    if (userId === currentUserId) return;

    queryClient.invalidateQueries(['feed']);
    queryClient.invalidateQueries(['posts']);
    queryClient.invalidateQueries(['post', postId]);
    queryClient.invalidateQueries(['likeStatus', postId]);
  });
};

/**
 * Register user status handlers
 * @param {Object} socket - Socket instance
 * @param {Function} setOnlineUsers - Online users state setter
 */
const registerUserStatusHandlers = (socket, setOnlineUsers) => {
  socket.on('get_users_online', users => {
    const map = {};
    if (Array.isArray(users)) users.forEach(id => (map[id] = true));
    else Object.assign(map, users || {});
    setOnlineUsers(map);
  });

  socket.on('user_status_change', ({ userId, status }) => {
    setOnlineUsers(prev => ({ ...prev, [userId]: status === 'online' }));
  });
};

/**
 * Register comment handlers
 * @param {Object} socket - Socket instance
 * @param {string} currentUserId - Current user ID
 * @param {Object} queryClient - React Query client
 */
const registerCommentHandlers = (socket, currentUserId, queryClient) => {
  socket.on('new_comment', data => {
    const { postId, userId } = data;
    if (userId === currentUserId) return;

    queryClient.invalidateQueries(['post', postId]);
    queryClient.invalidateQueries(['comments', postId]);
    queryClient.invalidateQueries(['posts']);
    queryClient.invalidateQueries(['feed']);
  });

  socket.on('delete_comment', data => {
    const { postId, userId } = data;
    if (userId === currentUserId) return;

    queryClient.invalidateQueries(['post', postId]);
    queryClient.invalidateQueries(['comments', postId]);
    queryClient.invalidateQueries(['posts']);
    queryClient.invalidateQueries(['feed']);
  });
};

export default useSocket;

import { useState, useRef, useCallback, useEffect } from 'react';
import { Link, Navigate, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { Sparkles, Mail, Lock, Eye, EyeOff, AlertCircle } from 'lucide-react';
import { login, googleAuth } from '@/redux/actions/authActions';
import { clearError } from '@/redux/slices/AuthSlice';
import toast from 'react-hot-toast';

const Login = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const googleButtonRef = useRef(null);
  const googleInitialized = useRef(false);
  const { loading, error, isAuthenticated, user } = useSelector(
    state => state.auth
  );

  const [showPassword, setShowPassword] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });

  // Redirect if already authenticated AND user data is loaded
  useEffect(() => {
    if (isAuthenticated && user) {
      navigate('/', { replace: true });
    }
  }, [isAuthenticated, user, navigate]);

  // Clear error on unmount

  // Initialize Google Sign-In
  const initGoogle = useCallback(() => {
    if (
      googleInitialized.current ||
      !window.google?.accounts?.id ||
      !googleButtonRef.current
    ) {
      return;
    }

    googleInitialized.current = true;

    window.google.accounts.id.initialize({
      client_id: import.meta.env.VITE_GOOGLE_CLIENT_ID,
      callback: async response => {
        if (response.credential) {
          const result = await dispatch(googleAuth(response.credential));
          if (googleAuth.fulfilled.match(result)) {
            toast.success('Đăng nhập Google thành công!');
          } else {
            toast.error('Đăng nhập Google thất bại');
          }
        }
      },
    });

    window.google.accounts.id.renderButton(googleButtonRef.current, {
      theme: 'outline',
      size: 'large',
      width: 320,
      text: 'continue_with',
      shape: 'pill',
    });
  }, [dispatch]);

  // Setup Google button when ref is available
  useEffect(() => {
    if (!googleButtonRef.current) return;

    // Try immediately
    initGoogle();

    // If not available, poll for it
    if (!googleInitialized.current) {
      const interval = setInterval(() => {
        if (window.google?.accounts?.id) {
          initGoogle();
          clearInterval(interval);
        }
      }, 200);

      const timeout = setTimeout(() => clearInterval(interval), 10000);
      return () => {
        clearInterval(interval);
        clearTimeout(timeout);
      };
    }
  }, [initGoogle]);

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (!formData.email || !formData.password) {
      toast.error('Vui lòng điền đầy đủ thông tin');
      return;
    }
    const result = await dispatch(login(formData));
    if (login.fulfilled.match(result)) {
      toast.success('Đăng nhập thành công!');
    }
  };

  // No longer needed due to useEffect redirect above
  // if (isAuthenticated && user) {
  //   return <Navigate to="/" replace />;
  // }

  return (
    <div className="min-h-screen flex">
      {/* Left Panel - Branding */}
      <div className="hidden lg:flex w-1/2 bg-black items-center justify-center relative overflow-hidden">
        <div className="absolute inset-0 bg-gradient-to-br from-black via-neutral-900 to-black" />

        {/* Decorative circles */}
        <div className="absolute top-20 left-20 w-64 h-64 bg-white/5 rounded-full blur-3xl" />
        <div className="absolute bottom-20 right-20 w-96 h-96 bg-white/3 rounded-full blur-3xl" />

        <div className="relative z-10 text-center px-12">
          <div className="w-16 h-16 rounded-full bg-white flex items-center justify-center mx-auto mb-8">
            <Sparkles size={28} className="text-black" />
          </div>
          <h1 className="text-5xl font-bold text-white mb-4 tracking-tight">
            YiBu
          </h1>
          <p className="text-white/60 text-lg max-w-sm mx-auto">
            Connect, share, and discover. Your social experience reimagined.
          </p>
        </div>
      </div>

      {/* Right Panel - Form */}
      <div className="w-full lg:w-1/2 flex items-center justify-center p-8 bg-white dark:bg-black">
        <div className="w-full max-w-md">
          {/* Mobile Logo */}
          <div className="lg:hidden flex items-center justify-center mb-10">
            <div className="w-12 h-12 rounded-full bg-black dark:bg-white flex items-center justify-center">
              <Sparkles size={22} className="text-white dark:text-black" />
            </div>
          </div>

          <div className="mb-10">
            <h2 className="text-3xl font-bold text-content dark:text-white mb-2">
              Welcome back
            </h2>
            <p className="text-neutral-500">Sign in to continue to YiBu</p>
          </div>

          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Email */}
            <div className="space-y-2">
              <label className="text-sm font-medium text-content dark:text-white">
                Email
              </label>
              <div className="relative">
                <Mail
                  size={18}
                  className="absolute left-4 top-1/2 -translate-y-1/2 text-neutral-400"
                />
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  placeholder="name@example.com"
                  className="w-full pl-12 pr-4 py-3.5 bg-neutral-50 dark:bg-neutral-900 border border-neutral-200 dark:border-neutral-800 rounded-xl text-content dark:text-white placeholder:text-neutral-400 focus:outline-none focus:border-primary transition-colors"
                />
              </div>
            </div>

            {/* Password */}
            <div className="space-y-2">
              <div className="flex items-center justify-between">
                <label className="text-sm font-medium text-content dark:text-white">
                  Password
                </label>
                <Link
                  to="/auth/forgot-password"
                  className="text-sm text-neutral-500 hover:text-black dark:hover:text-white transition-colors"
                >
                  Forgot?
                </Link>
              </div>
              <div className="relative">
                <Lock
                  size={18}
                  className="absolute left-4 top-1/2 -translate-y-1/2 text-neutral-400"
                />
                <input
                  type={showPassword ? 'text' : 'password'}
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  placeholder="Enter your password"
                  className="w-full pl-12 pr-12 py-3.5 bg-neutral-50 dark:bg-neutral-900 border border-neutral-200 dark:border-neutral-800 rounded-xl text-content dark:text-white placeholder:text-neutral-400 focus:outline-none focus:border-primary transition-colors"
                />
                <button
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  className="absolute right-4 top-1/2 -translate-y-1/2 text-neutral-400 hover:text-neutral-600 dark:hover:text-neutral-300"
                >
                  {showPassword ? <EyeOff size={18} /> : <Eye size={18} />}
                </button>
              </div>
            </div>

            {/* Error Message */}
            {error && (
              <div className="flex items-center gap-2 p-3 bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800 rounded-xl text-red-600 dark:text-red-400 text-sm">
                <AlertCircle size={16} />
                {error}
              </div>
            )}

            {/* Submit */}
            <button
              type="submit"
              disabled={loading}
              className="w-full py-3.5 bg-primary text-primary-foreground font-medium rounded-xl hover:opacity-90 transition-opacity disabled:opacity-50 flex items-center justify-center gap-2"
            >
              {loading ? (
                <div className="w-5 h-5 border-2 border-primary-foreground/30 border-t-primary-foreground rounded-full animate-spin" />
              ) : (
                'Sign In'
              )}
            </button>

            {/* Divider */}
            <div className="relative flex items-center my-8">
              <div className="flex-1 border-t border-neutral-200 dark:border-neutral-800" />
              <span className="px-4 text-sm text-neutral-400">or</span>
              <div className="flex-1 border-t border-neutral-200 dark:border-neutral-800" />
            </div>

            {/* Google Sign-In Button */}
            <div className="flex justify-center">
              <div ref={googleButtonRef} />
            </div>
          </form>

          {/* Footer */}
          <p className="mt-10 text-center text-neutral-500">
            Don't have an account?{' '}
            <Link
              to="/auth/register"
              className="font-medium text-primary hover:underline"
            >
              Sign up
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;

import { useState, useEffect, useMemo } from 'react';
import { useNavigate, Outlet, useParams } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { Search, Settings, MessageCircle, Loader2, Users } from 'lucide-react';
import {
  useConversations,
  useCreateConversation,
  useCreateGroup,
} from '@/hooks/useMessageQuery';
import { useSearchUsers } from '@/hooks/useSearchQuery';
import ConversationItem from '@/components/features/chat/ConversationItem';
import CreateGroupModal from '@/components/features/chat/CreateGroupModal';
import { useDebounce } from '@/hooks/useDebounce';

function Message() {
  const navigate = useNavigate();
  const { conversationId } = useParams();

  const { data: conversationsData, isLoading: conversationsLoading } =
    useConversations();
  const conversations =
    conversationsData?.conversations || conversationsData || [];

  const { user } = useSelector(state => state.auth);

  const [searchQuery, setSearchQuery] = useState('');
  const [showGroupModal, setShowGroupModal] = useState(false);
  const [groupName, setGroupName] = useState('');
  const [userSearchTerm, setUserSearchTerm] = useState('');
  const [selectedUsers, setSelectedUsers] = useState([]);
  const debouncedUserSearch = useDebounce(userSearchTerm, 500);

  const createConversationMutation = useCreateConversation();
  const createGroupMutation = useCreateGroup();

  const { data: searchResultsData, isLoading: isSearchingUsers } =
    useSearchUsers({
      query: debouncedUserSearch,
      page: 1,
      limit: 5,
    });
  const searchResults = searchResultsData?.users || searchResultsData || [];

  // Handle Deep Linking / Sync Route
  useEffect(() => {
    const handleSync = async () => {
      if (!conversationId || conversationId === 'messages') return;

      const existing = conversations.find(
        c =>
          c._id === conversationId ||
          c.conversationId === conversationId ||
          c.id === conversationId ||
          c.directId === conversationId
      );

      if (
        existing &&
        conversationId.includes('_') &&
        existing._id &&
        existing._id !== conversationId
      ) {
        navigate(`/messages/${existing._id}`, { replace: true });
        return;
      }

      if (!existing && !conversationsLoading) {
        // Match user1Id_user2Id format
        const pairMatch = conversationId.match(
          /^([a-f\d]{24})_([a-f\d]{24})$/i
        );
        if (pairMatch) {
          const [_, u1, u2] = pairMatch;
          const targetId = u1 === user?._id ? u2 : u1;
          if (targetId) {
            try {
              const conv = await createConversationMutation.mutateAsync(
                targetId
              );
              if (conv?._id && conv?._id !== conversationId) {
                navigate(`/messages/${conv._id}`, { replace: true });
              }
            } catch (err) {
              console.error('Failed to create/fetch conversation by pair', err);
            }
          }
        }
      }
    };

    if (user) handleSync();
  }, [
    conversationId,
    conversations,
    user,
    conversationsLoading,
    navigate,
    createConversationMutation,
  ]);

  const filteredConversations = useMemo(() => {
    if (!Array.isArray(conversations)) return [];
    return conversations.filter(c => {
      const otherUser =
        c.otherUser ||
        c.participant ||
        c.members?.find(m => m._id !== user?._id);
      const name = c.isGroup
        ? c.name || 'Nhóm'
        : otherUser?.name || otherUser?.fullName || otherUser?.username || '';
      return name.toLowerCase().includes(searchQuery.toLowerCase());
    });
  }, [conversations, searchQuery, user?._id]);

  const handleCreateGroup = async () => {
    if (!groupName.trim() || selectedUsers.length < 2) return;
    try {
      const newGroup = await createGroupMutation.mutateAsync({
        name: groupName,
        participantIds: selectedUsers.map(u => u._id),
      });

      setShowGroupModal(false);
      setGroupName('');
      setSelectedUsers([]);
      navigate(`/messages/${newGroup._id || newGroup.id}`);
    } catch (error) {
      console.error('Failed to create group:', error);
    }
  };

  const toggleUserSelection = user => {
    if (selectedUsers.find(u => u._id === user._id)) {
      setSelectedUsers(selectedUsers.filter(u => u._id !== user._id));
    } else {
      setSelectedUsers([...selectedUsers, user]);
    }
  };

  return (
    <div className="flex w-full h-[calc(100vh-4rem)] md:h-screen bg-transparent overflow-hidden">
      {/* Left Sidebar (Conversation List) */}
      <div
        className={`w-full md:w-[380px] lg:w-[400px] flex-shrink-0 border-r border-border flex flex-col bg-background transition-all ${
          conversationId ? 'hidden md:flex' : 'flex'
        }`}
      >
        {/* Header */}
        <div className="p-5 border-b border-border">
          <div className="flex items-center justify-between mb-6">
            <h1 className="text-2xl font-black text-content tracking-tight">
              Tin nhắn
            </h1>
            <div className="flex items-center gap-1">
              <button
                onClick={() => setShowGroupModal(true)}
                className="yb-btn-ghost p-2.5 rounded-full transition-all text-secondary hover:text-primary"
                title="Tạo nhóm chat"
              >
                <Users size={22} />
              </button>
              <button className="yb-btn-ghost p-2.5 rounded-full transition-all text-secondary hover:text-primary">
                <Settings size={22} />
              </button>
            </div>
          </div>
          {/* Search */}
          <div className="relative group">
            <Search
              size={18}
              className="absolute left-4 top-1/2 -translate-y-1/2 text-text-tertiary group-focus-within:text-primary transition-colors"
            />
            <input
              type="text"
              value={searchQuery}
              onChange={e => setSearchQuery(e.target.value)}
              placeholder="Tìm kiếm cuộc trò chuyện..."
              className="w-full pl-11 pr-4 py-3 rounded-2xl bg-surface-secondary text-content border border-transparent focus:border-border-focus placeholder:text-text-tertiary text-sm focus:outline-none transition-all duration-300"
            />
          </div>
        </div>

        {/* Conversations List */}
        <div className="flex-1 overflow-y-auto custom-scrollbar pb-20 md:pb-0">
          {conversationsLoading && conversations.length === 0 ? (
            <div className="flex flex-col items-center justify-center py-12 space-y-4">
              <Loader2 className="w-8 h-8 animate-spin text-primary" />
              <p className="text-xs text-text-tertiary font-medium">
                Đang tải cuộc trò chuyện...
              </p>
            </div>
          ) : filteredConversations.length === 0 ? (
            <div className="p-10 text-center flex flex-col items-center justify-center space-y-4">
              <div className="w-16 h-16 bg-surface-secondary rounded-3xl flex items-center justify-center border border-border">
                <MessageCircle size={32} className="text-text-tertiary" />
              </div>
              <p className="text-text-tertiary text-sm font-medium">
                {searchQuery
                  ? 'Không tìm thấy kết quả'
                  : 'Chưa có cuộc trò chuyện nào'}
              </p>
            </div>
          ) : (
            <div className="">
              {filteredConversations.map(conversation => {
                const id =
                  conversation._id ||
                  conversation.conversationId ||
                  conversation.id;
                return (
                  <ConversationItem
                    key={id}
                    conversation={conversation}
                    isActive={
                      !!conversationId &&
                      (conversationId === id ||
                        conversation.directId === conversationId)
                    }
                    onClick={() => navigate(`/messages/${id}`)}
                    currentUserId={user?._id}
                  />
                );
              })}
            </div>
          )}
        </div>
      </div>

      {/* Right Content Area (Rendered via Outlet) */}
      <div
        className={`flex-1 min-w-0 bg-background flex flex-col ${
          conversationId ? 'flex' : 'hidden md:flex'
        }`}
      >
        <Outlet />
      </div>

      {/* Create Group Modal */}
      <CreateGroupModal
        isOpen={showGroupModal}
        onClose={() => setShowGroupModal(false)}
        groupName={groupName}
        setGroupName={setGroupName}
        searchQuery={userSearchTerm}
        onSearchChange={setUserSearchTerm}
        searchResults={searchResults}
        selectedUsers={selectedUsers}
        onToggleUser={toggleUserSelection}
        isSearching={isSearchingUsers}
        onSubmit={handleCreateGroup}
      />
    </div>
  );
}

export default Message;

import { createSlice } from '@reduxjs/toolkit';
import {
  login,
  register,
  googleAuth,
  logout,
  logoutAll,
  updatePassword,
  requestPasswordReset,
  resetPassword,
  enable2FA,
  verify2FA,
  disable2FA,
  getSessions,
  revokeSession,
} from '@/redux/actions/authActions';

const initialState = {
  user: null,
  isAuthenticated: false,
  loading: false,
  error: null,
  sessions: [],
  twoFactorEnabled: false,
  twoFactorPending: false,
};

const authSlice = createSlice({
  name: 'auth',
  initialState,
  reducers: {
    clearError: state => {
      state.error = null;
    },
    setUser: (state, action) => {
      state.user = action.payload;
      state.isAuthenticated = !!action.payload;
    },
    setIsAuthenticated: (state, action) => {
      state.isAuthenticated = action.payload;
    },
    updateUserProfile: (state, action) => {
      if (state.user) {
        state.user = { ...state.user, ...action.payload };
      }
    },
    resetAuthState: () => initialState,
  },
  extraReducers: builder => {
    builder
      // Login
      .addCase(login.pending, state => {
        state.loading = true;
        state.error = null;
      })
      .addCase(login.fulfilled, (state, action) => {
        state.loading = false;
        state.user = action.payload;
        state.isAuthenticated = true;
        state.twoFactorEnabled = action.payload?.twoFactorEnabled || false;
      })
      .addCase(login.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      })
      // Register
      .addCase(register.pending, state => {
        state.loading = true;
        state.error = null;
      })
      .addCase(register.fulfilled, (state, action) => {
        state.loading = false;
        state.user = action.payload;
        state.isAuthenticated = true;
      })
      .addCase(register.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      })
      // Google Auth
      .addCase(googleAuth.pending, state => {
        state.loading = true;
        state.error = null;
      })
      .addCase(googleAuth.fulfilled, (state, action) => {
        state.loading = false;
        state.user = action.payload;
        state.isAuthenticated = true;
      })
      .addCase(googleAuth.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      })
      // Logout
      .addCase(logout.fulfilled, () => initialState)
      .addCase(logout.rejected, () => initialState)
      .addCase(logoutAll.fulfilled, () => initialState)
      // Update Password
      .addCase(updatePassword.pending, state => {
        state.loading = true;
        state.error = null;
      })
      .addCase(updatePassword.fulfilled, state => {
        state.loading = false;
      })
      .addCase(updatePassword.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      })
      // Password Reset Request
      .addCase(requestPasswordReset.pending, state => {
        state.loading = true;
        state.error = null;
      })
      .addCase(requestPasswordReset.fulfilled, state => {
        state.loading = false;
      })
      .addCase(requestPasswordReset.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      })
      // Reset Password
      .addCase(resetPassword.pending, state => {
        state.loading = true;
        state.error = null;
      })
      .addCase(resetPassword.fulfilled, state => {
        state.loading = false;
      })
      .addCase(resetPassword.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      })
      // 2FA Enable
      .addCase(enable2FA.pending, state => {
        state.loading = true;
      })
      .addCase(enable2FA.fulfilled, (state, action) => {
        state.loading = false;
        state.twoFactorPending = true;
      })
      .addCase(enable2FA.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      })
      // 2FA Verify
      .addCase(verify2FA.pending, state => {
        state.loading = true;
      })
      .addCase(verify2FA.fulfilled, state => {
        state.loading = false;
        state.twoFactorEnabled = true;
        state.twoFactorPending = false;
      })
      .addCase(verify2FA.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      })
      // 2FA Disable
      .addCase(disable2FA.fulfilled, state => {
        state.twoFactorEnabled = false;
      })
      // Sessions
      .addCase(getSessions.fulfilled, (state, action) => {
        // Handle { sessions } or direct array
        state.sessions =
          action.payload?.sessions ||
          (Array.isArray(action.payload) ? action.payload : []);
      })
      .addCase(revokeSession.fulfilled, (state, action) => {
        state.sessions = state.sessions.filter(
          session => session.id !== action.payload.sessionId
        );
      });
  },
});

export const {
  clearError,
  setUser,
  setIsAuthenticated,
  updateUserProfile,
  resetAuthState,
} = authSlice.actions;
export default authSlice.reducer;
